pragma solidity ^0.8.9;
pragma experimental ABIEncoderV2;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {IDetailedERC20} from "./interfaces/IDetailedERC20.sol";
import {IERC3156FlashBorrower} from "./interfaces/IERC3156FlashBorrower.sol";
import {IERC3156FlashLender} from "./interfaces/IERC3156FlashLender.sol";

/// @title AlchemicTokenV2
///
/// @dev This is the contract for the Alchemix utility token usd.
///
/// Initially, the contract deployer is given both the admin and minter role. This allows them to
/// pre-mine tokens, transfer admin to a timelock contract, and lastly, grant the staking pools the
/// minter role. After this is done, the deployer must revoke their admin role and minter role.
contract AlchemicTokenV2 is AccessControl, ReentrancyGuard, ERC20, IERC3156FlashLender {
  using SafeERC20 for ERC20;
  using SafeMath for uint256;

  /// @dev The identifier of the role which maintains other roles.
  bytes32 public constant ADMIN_ROLE = keccak256("ADMIN");

  /// @dev The identifier of the role which allows accounts to mint tokens.
  bytes32 public constant SENTINEL_ROLE = keccak256("SENTINEL");

  /// @dev The expected return value from a flashmint receiver
  bytes32 public constant CALLBACK_SUCCESS = keccak256("ERC3156FlashBorrower.onFlashLoan");

  /// @dev addresses whitelisted for minting new tokens
  mapping(address => bool) public whitelisted;

  /// @dev addresses paused for minting new tokens
  mapping(address => bool) public paused;

  /// @dev ceiling per address for minting new tokens
  mapping(address => uint256) public mintCeiling;

  /// @dev already minted amount per address to track the ceiling
  mapping(address => uint256) public totalMinted;

  event Paused(address minter, bool state);

  constructor(string memory name, string memory symbol) ERC20(name, symbol) {
    _setupRole(ADMIN_ROLE, msg.sender);
    _setupRole(SENTINEL_ROLE, msg.sender);
    _setRoleAdmin(SENTINEL_ROLE, ADMIN_ROLE);
    _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
  }

  /// @dev A modifier which checks that the caller has the admin role.
  modifier onlyAdmin() {
    require(hasRole(ADMIN_ROLE, msg.sender), "only admin");
    _;
  }
  /// @dev A modifier which checks that the caller has the sentinel role.
  modifier onlySentinel() {
    require(hasRole(SENTINEL_ROLE, msg.sender), "only sentinel");
    _;
  }

  /// @dev A modifier which checks if whitelisted for minting.
  modifier onlyWhitelisted() {
    require(whitelisted[msg.sender], "alX: Alchemist is not whitelisted");
    _;
  }

  /// @dev Mints tokens to a recipient.
  ///
  /// This function reverts if the caller does not have the minter role.
  ///
  /// @param recipient the account to mint tokens to.
  /// @param amount    the amount of tokens to mint.
  function mint(address recipient, uint256 amount) external onlyWhitelisted {
    require(!paused[msg.sender], "alX: Alchemist is currently paused.");

    uint256 total = amount + totalMinted[msg.sender];
    require(total <= mintCeiling[msg.sender], "alX: Alchemist's ceiling was breached.");
    totalMinted[msg.sender] = total;

    _mint(recipient, amount);
  }

  /// @dev This function reverts if the caller does not have the admin role.
  ///
  /// @param who the account to mint tokens to.
  /// @param state the whitelist state.
  function setWhitelist(address who, bool state) external onlyAdmin {
    whitelisted[who] = state;
  }

  /// @dev This function reverts if the caller does not have the admin role.
  ///
  /// @param newSentinel the account to set as sentinel.
  function setSentinel(address newSentinel) external onlyAdmin {
    _setupRole(SENTINEL_ROLE, newSentinel);
  }

  /// @dev This function reverts if the caller does not have the admin role.
  ///
  /// @param minter  the account set the ceiling off.
  /// @param ceiling the max amount of tokens the account is allowed to mint.
  function setCeiling(address minter, uint256 ceiling) external onlyAdmin {
    mintCeiling[minter] = ceiling;
  }

  /// @dev This function reverts if the caller does not have the admin role.
  ///
  /// @param minter the account with minting permissions.
  /// @param state  the state of the minter permissions.
  function pauseMinter(address minter, bool state) external onlySentinel {
    paused[minter] = state;
    emit Paused(minter, state);
  }

  /// @dev Destroys `amount` tokens from the caller.
  ///
  /// @param amount the amount of tokens to be burned.
  function burn(uint256 amount) external {
    _burn(_msgSender(), amount);
  }

  /// @dev Destroys `amount` tokens from `account`, deducting from the caller's allowance.
  ///
  /// @param account the account from which tokens will be burned.
  /// @param amount  the amount of tokens to be burned.
  function burnFrom(address account, uint256 amount) external {
    uint256 newAllowance = allowance(account, _msgSender()).sub(amount, "AlToken: burn amount exceeds allowance");

    _approve(account, _msgSender(), newAllowance);
    _burn(account, amount);
  }

  /// @dev Lowers hasMinted from the caller's allocation.
  ///
  /// @param amount the amount of tokens to subtract from totalMinted.
  function lowerHasMinted(uint256 amount) external onlyWhitelisted {
    totalMinted[msg.sender] = totalMinted[msg.sender] - amount;
  }

  /// @dev Maximum amount to be flashloaned
  ///
  /// @param token The address of the token
  function maxFlashLoan(address token) public view override returns (uint256) {
    require(token == address(this), "Can only flashmint self");
    return type(uint256).max;
  }

  /// @dev The fee for flashloan
  ///
  /// @param token The address of the token
  /// @param amount How much token to flashmint
  function flashFee(address token, uint256 amount) public view override returns (uint256) {
    require(token == address(this), "Can only flashmint self");
    return amount / 1000; // 0.1%, we could modify this
  }

  /// @dev Perform a flashmint (called flashloan to confirm with ERC3156 standard)
  ///
  /// @param receiver The callback address
  /// @param token The address of the token
  /// @param amount How much token to flashmint
  /// @param data Extra data to pass into callback
  function flashLoan(
    IERC3156FlashBorrower receiver,
    address token,
    uint256 amount,
    bytes calldata data
  ) external override nonReentrant returns (bool) {
    require(token == address(this), "Can only flashmint self");
    require(amount <= maxFlashLoan(token), "Surpasses max flashmint amount");

    uint256 fee = flashFee(token, amount);

    _mint(address(receiver), amount);
    require(receiver.onFlashLoan(msg.sender, token, amount, fee, data) == CALLBACK_SUCCESS, "flashLoan failed");
    _burn(address(receiver), amount + fee); // Will throw error if not enough to burn

    return true;
  }
}
","keccak256":"0x4cd65baf6efd974c4fc50dba45aa2fa4357d2daf196c5fce20f2f4313a7da88e","license":"GPL-3.0-or-later"},"contracts/AlchemistV2.sol":{"content":"pragma solidity ^0.8.9;

import "./interfaces/IAlchemistV2.sol";
import "./interfaces/IERC20Minimal.sol";
import "./interfaces/IERC20TokenReceiver.sol";
import "./interfaces/ITokenAdapter.sol";
import "./AlchemicTokenV2.sol";
import "./libraries/SafeCast.sol";
import "./libraries/Sets.sol";
import "./libraries/TokenUtils.sol";
import "./libraries/Limiters.sol";
import "./base/Multicall.sol";
import "./base/Mutex.sol";
import "./ContractWhitelister.sol";

/// @title  AlchemistV2
/// @author Alchemix Finance
contract AlchemistV2 is IAlchemistV2, Multicall, Mutex, ContractWhitelister {
    using Limiters for Limiters.LinearGrowthLimiter;
    using Sets for Sets.AddressSet;

    /// @notice The maximum precision of percentage calculations.
    uint256 public constant MAX_BPS = 10000;

    /// @notice Alchemist configuration params.
    struct Params {
        address admin;                      /// The admin account.
        address debtToken;                  /// The ERC20 token used to represent debt in the Alchemist.
        address transmuter;                 /// The Transmuter (or TransmuterBuffer) used by the Alchemist.
        uint256 minimumCollateralization;   /// The minimum collateralization ratio that a CDP must maintain.
        uint256 protocolFee;                /// The percentage fee taken from each harvest.
        address protocolFeeReceiver;        /// The address that receives the fee.
        uint256 mintingLimitMaximum;        /// The limit to be used in the minting limiter.
        uint256 mintingLimitBlocks;         /// The number of recovery blocks to be used in the minting limiter.
    }

    /// @notice Parameters and state of an underlying-token.
    struct UnderlyingTokenParams {
        uint8 decimals;                                     /// The decimals of the underlying-token.
        uint256 conversionFactor;                           /// A number used to normalize the decimals of the underlying-token.
        Limiters.LinearGrowthLimiter repayLimiter;        /// An LGF used to limit repayments.
        Limiters.LinearGrowthLimiter liquidationLimiter;  /// An LGF used to limit liquidations.
        bool enabled;                                       /// A flag to represent if the underlying-token is enabled.
    }

    /// @notice Parameters used to configure a yield-token.
    struct YieldTokenParams {
        uint8 decimals;                 /// The decimals of the yield-token.
        address underlyingToken;        /// The associated underlying-token that can be redeemed for the yield-token.
        address adapter;                /// The adapter used by the Alchemist to wrap and unwarp the yield-token.
        uint256 maximumLoss;            /// The maximum percentage loss that is acceptable befor disabling most interations with yield-token via the Alchemist.
        uint256 maximumExpectedValue;   /// The maximum value of yield-tokens that the Alchemist can hold, represented in underlying-tokens.
        uint256 balance;                /// The current balance of yield-tokens in the Alchemist.
        uint256 totalShares;            /// The total number of shares that have been minted for this token.
        uint256 expectedValue;          /// The expected number of underlying-tokens that can be obtained by redeeming the held yield-tokens.
        uint256 accruedWeight;          /// The total accrued weight of yield-tokens in the Alchemist.
        bool enabled;                   /// A flag to represent if the yield-token is enabled.
    }

    /// @notice A CDP
    struct Account {        
        int256 debt;                                    /// The debt held by the CDP.
        mapping(address => uint256) balances;           /// The balances of yield-tokens held by the CDP.
        mapping(address => uint256) lastAccruedWeights; /// The last values recorded for accrued weights of yield-tokens held by the CDP.
        Sets.AddressSet depositedTokens;                /// The yield-tokens deposited in the CDP.
        mapping(address => uint256) mintAllowances;     /// The allowances of third-party minters to mint debt from the CDP.
        mapping(address => uint256) withdrawAllowances; /// The allowances of third-party collateral withdrawals to withdrawal collateral from the CDP.
    }

    /// @inheritdoc IAlchemistV2Immutables
    address public immutable override debtToken;

    /// @inheritdoc IAlchemistV2State
    address public override admin;

    /// @inheritdoc IAlchemistV2State
    address public override pendingAdmin;

    /// @inheritdoc IAlchemistV2State
    mapping(address => bool) public override sentinels;

    /// @inheritdoc IAlchemistV2State
    mapping(address => bool) public override keepers;

    /// @inheritdoc IAlchemistV2State
    address public override transmuter;

    /// @inheritdoc IAlchemistV2State
    uint256 public override minimumCollateralization;

    /// @inheritdoc IAlchemistV2State
    uint256 public override protocolFee;

    /// @inheritdoc IAlchemistV2State
    address public override protocolFeeReceiver;

    /// @inheritdoc IAlchemistV2Permit
    // keccak256(MintFrom(address owner,uint256 amount,address recipient))
    bytes32 public constant override PERMIT_MINT_TYPEHASH = 0x75597f45d20ee38c23b459da2dc911cec482e56521830ddc96918afde28f35e2;

    /// @inheritdoc IAlchemistV2Permit
    // keccak256(WithdrawFrom(address owner,address token,uint256 shares,address recipient))
    bytes32 public constant override PERMIT_WITHDRAW_TYPEHASH = 0x4e69b929e93dac11cc23d4346204322b15f9aeb082793d7c74e14af3073a48c9;

    /// @inheritdoc IAlchemistV2Permit
    mapping(address => uint256) public override nonces;

    /// @dev A linear growth function that limits the amount of debt-token minted.
    Limiters.LinearGrowthLimiter private _mintingLimiter;

    /// @dev Accounts mapped by the address that owns them.
    mapping(address => Account) private _accounts;

    /// @dev Underlying token parameters mapped by token address.
    mapping(address => UnderlyingTokenParams) private _underlyingTokens;

    /// @dev Yield token parameters mapped by token address.
    mapping(address => YieldTokenParams) private _yieldTokens;

    /// @dev An iterable set of the underlying tokens that are supported by the system.
    Sets.AddressSet private _supportedUnderlyingTokens;

    /// @dev An iterable set of the yield tokens that are supported by the system.
    Sets.AddressSet private _supportedYieldTokens;

    constructor(Params memory params) ContractWhitelister() {
        debtToken = params.debtToken;
        admin = params.admin;
        transmuter = params.transmuter;
        minimumCollateralization = params.minimumCollateralization;
        protocolFee = params.protocolFee;
        protocolFeeReceiver = params.protocolFeeReceiver;

        _mintingLimiter = Limiters.createLinearGrowthLimiter(params.mintingLimitMaximum, params.mintingLimitBlocks);

        whitelistActive = true;

        emit AdminUpdated(admin);
        emit TransmuterUpdated(transmuter);
        emit MinimumCollateralizationUpdated(minimumCollateralization);
        emit ProtocolFeeUpdated(protocolFee);
        emit ProtocolFeeReceiverUpdated(protocolFeeReceiver);
        emit MintingLimitUpdated(params.mintingLimitMaximum, params.mintingLimitBlocks);
    }

    /// @dev Only calls from the admin address are authorized to pass.
    function onlyAdmin() internal override {
        if (msg.sender != admin) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert Unauthorized("Not admin");
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Only calls from a sentinel address are authorized to pass.
    function onlySentinelOrAdmin() internal {
        if (!sentinels[msg.sender] && msg.sender != admin) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert Unauthorized("Not sentinel");
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Only calls from a keeper address are authorized to pass.
    modifier onlyKeeper() {
        if (!keepers[msg.sender]) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert OnlyKeeper();
            // #else
            require(false);
            // #endif
        }
        _;
    }

    /// @inheritdoc IAlchemistV2State
    function yieldTokensPerShare(address yieldToken) external view override returns (uint256) {
        return _convertSharesToYieldTokens(yieldToken, 10** _yieldTokens[yieldToken].decimals);
    }

    /// @inheritdoc IAlchemistV2State
    function underlyingTokensPerShare(address yieldToken) external view override returns (uint256) {
        return _convertSharesToUnderlyingTokens(yieldToken, 10** _yieldTokens[yieldToken].decimals);
    }

    /// @inheritdoc IAlchemistV2State
    function getSupportedUnderlyingTokens() external view override returns (address[] memory) {
        return _supportedUnderlyingTokens.values;
    }

    /// @inheritdoc IAlchemistV2State
    function getSupportedYieldTokens() external view override returns (address[] memory) {
        return _supportedYieldTokens.values;
    }

    /// @inheritdoc IAlchemistV2State
    function isSupportedUnderlyingToken(address underlyingToken) external view override returns (bool) {
        return _supportedUnderlyingTokens.contains(underlyingToken);
    }

    /// @inheritdoc IAlchemistV2State
    function isSupportedYieldToken(address yieldToken) external view override returns (bool) {
        return _supportedYieldTokens.contains(yieldToken);
    }

    /// @inheritdoc IAlchemistV2State
    function getUnderlyingTokenParameters(address underlyingToken)
        external view override
        returns (
            uint8 decimals,
            uint256 conversionFactor,
            bool enabled
        )
    {
        UnderlyingTokenParams storage params = _underlyingTokens[underlyingToken];
        return (
            params.decimals,
            params.conversionFactor,
            params.enabled
        );
    }

    /// @inheritdoc IAlchemistV2State
    function accounts(address owner) external view override returns (int256 debt, address[] memory depositedTokens) {
        Account storage account = _accounts[owner];
        return (account.debt, account.depositedTokens.values);
    }

    /// @inheritdoc IAlchemistV2State
    function positions(
        address owner,
        address yieldToken
    ) external view override returns (uint256 balance, uint256 lastAccruedWeight) {
        Account storage account = _accounts[owner];
        return (account.balances[yieldToken], account.lastAccruedWeights[yieldToken]);
    }

    /// @inheritdoc IAlchemistV2State
    function getYieldTokenParameters(address yieldToken)
        external view override
        returns (
            uint8 decimals,
            address underlyingToken,
            address adapter,
            uint256 maximumLoss,
            uint256 balance,
            uint256 totalShares,
            uint256 expectedValue,
            uint256 accruedWeight,
