    }

    /// @inheritdoc IAlchemistV2Permit
    function DOMAIN_SEPARATOR() public view override returns (bytes32) {
        return keccak256(
            abi.encode(
                // keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)")
                0x8b73c3c69bb8fe3d512ecc4cf759cc79239f7b179b0ffacaa9a75d522b39400f,
                // keccak256("Alchemix - Alchemist V2")
                0xfc01e36e23316d5c2b9890d52c673e909cb50865d3d49aeed293bb9a5fb8d888,
                // keccak256("1")
                0xc89efdaa54c0f20c7adf612882df0950f5a951637e0307cdcb4c672f298b8bc6,
                block.chainid,
                address(this)
            )
        );
    }

    /// @inheritdoc IAlchemistV2AdminActions
    function setPendingAdmin(address value) external override {
        onlyAdmin();
        pendingAdmin = value;
        emit PendingAdminUpdated(value);
    }

    /// @inheritdoc IAlchemistV2AdminActions
    function acceptAdmin() external override {
        if (pendingAdmin == address(0)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("No pending admin is set");
            // #else
            require(false);
            // #endif
        }

        if (msg.sender != pendingAdmin) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert Unauthorized("Only pending admin may call");
            // #else
            require(false);
            // #endif
        }

        admin = pendingAdmin;
        pendingAdmin = address(0);

        emit AdminUpdated(admin);
        emit PendingAdminUpdated(address(0));
    }

    function addSentinel(address value) external {
        onlyAdmin();
        if (sentinels[value]) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Sentinel already added");
            // #else
            require(false);
            // #endif
        }
        sentinels[value] = true;
        emit SentinelAdded(value);
    }

    function removeSentinel(address value) external {
        onlyAdmin();
        if (!sentinels[value]) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Sentinel not added yet");
            // #else
            require(false);
            // #endif
        }
        sentinels[value] = false;
        emit SentinelRemoved(value);
    }

    function addKeeper(address value) external {
        onlyAdmin();
        if (keepers[value]) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Keeper already added");
            // #else
            require(false);
            // #endif
        }
        keepers[value] = true;
        emit KeeperAdded(value);
    }

    function removeKeeper(address value) external {
        onlyAdmin();
        if (!keepers[value]) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Keeper not added yet");
            // #else
            require(false);
            // #endif
        }
        keepers[value] = false;
        emit KeeperRemoved(value);
    }

    /// @inheritdoc IAlchemistV2AdminActions
    function addUnderlyingToken(address token, UnderlyingTokenConfig calldata config) external override lock {
        onlyAdmin();
        if (_supportedUnderlyingTokens.contains(token)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Token already added");
            // #else
            require(false);
            // #endif
        }

        uint8 tokenDecimals = TokenUtils.expectDecimals(token);
        uint8 debtTokenDecimals = TokenUtils.expectDecimals(debtToken);

        if (tokenDecimals > debtTokenDecimals) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalArgument("Token has unsupported number of decimals");
            // #else
            require(false);
            // #endif
        }

        _underlyingTokens[token] = UnderlyingTokenParams({
            decimals: tokenDecimals,
            conversionFactor: 10**(debtTokenDecimals - tokenDecimals),
            repayLimiter: Limiters.createLinearGrowthLimiter(config.repayLimitMaximum, config.repayLimitBlocks),
            liquidationLimiter: Limiters.createLinearGrowthLimiter(config.liquidationLimitMaximum, config.liquidationLimitBlocks),
            enabled: false
        });

        _supportedUnderlyingTokens.add(token);

        emit AddUnderlyingToken(token);
    }

    /// @inheritdoc IAlchemistV2AdminActions
    function addYieldToken(address token, YieldTokenConfig calldata config) external override lock {
        onlyAdmin();
        if (config.maximumLoss > MAX_BPS) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalArgument("Maximum loss exceeds maximum supported value");
            // #else
            require(false);
            // #endif
        }

        ITokenAdapter adapter = ITokenAdapter(config.adapter);

        if (token != adapter.token()) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Adapter token mismatch");
            // #else
            require(false);
            // #endif
        }

        if (_supportedYieldTokens.contains(token)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Yield token is already added");
            // #else
            require(false);
            // #endif
        }

        if (!_supportedUnderlyingTokens.contains(adapter.underlyingToken())) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Underlying token of yield token is not supported");
            // #else
            require(false);
            // #endif
        }

        _yieldTokens[token] = YieldTokenParams({
            decimals: TokenUtils.expectDecimals(token),
            underlyingToken: adapter.underlyingToken(),
            adapter: config.adapter,
            maximumLoss: config.maximumLoss,
            maximumExpectedValue: config.maximumExpectedValue,
            balance: 0,
            totalShares: 0,
            expectedValue: 0,
            accruedWeight: 0,
            enabled: false
        });

        _supportedYieldTokens.add(token);

        TokenUtils.safeApprove(token, config.adapter, type(uint256).max);
        TokenUtils.safeApprove(adapter.underlyingToken(), config.adapter, type(uint256).max);

        emit AddYieldToken(token, config.adapter);
        emit MaximumLossUpdated(token, config.maximumLoss);
    }

    /// @inheritdoc IAlchemistV2AdminActions
    function enableUnderlyingToken(address underlyingToken) external override {
        onlyAdmin();
        if (_underlyingTokens[underlyingToken].enabled) {
            // #if !(DISABLE_CUSTOM_ERRORS)
