            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Underlying token already disabled");
            // #else
            require(false);
            // #endif
        }
        _underlyingTokens[underlyingToken].enabled = false;
        emit UnderlyingTokenDisabled(underlyingToken);
    }

