    /// @inheritdoc IAlchemistV2AdminActions
    function setMaximumLoss(address yieldToken, uint256 value) external override {
        onlyAdmin();
        if (value > MAX_BPS) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalArgument("Exceeds maximum supported value");
            // #else
            require(false);
            // #endif
        }

        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false);
            // #endif
        }

        _yieldTokens[yieldToken].maximumLoss = value;
        emit MaximumLossUpdated(yieldToken, value);
    }

    /// @inheritdoc IAlchemistV2AdminActions
    function snap(address yieldToken) external override lock {
        onlyAdmin();
        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false);
            // #endif
        }
        uint256 currentBalance = _yieldTokens[yieldToken].balance;
        uint256 expectedValue = _convertYieldTokensToUnderlying(yieldToken, currentBalance);
        _yieldTokens[yieldToken].expectedValue = expectedValue;
        emit Snap(yieldToken, expectedValue);
    }

    /// @inheritdoc IAlchemistV2Actions
    function approveMint(address spender, uint256 amount) external override {
        onlyWhitelisted();
        _approveMint(msg.sender, spender, amount);
    }

    /// @inheritdoc IAlchemistV2Actions
    function approveWithdraw(address withdrawer, uint256 amount) external override {
        onlyWhitelisted();
        _approveWithdraw(msg.sender, withdrawer, amount);
    }

    /// @inheritdoc IAlchemistV2Actions
    function poke(address owner) external override lock {
        _poke(owner);
    }

    /// @inheritdoc IAlchemistV2Actions
    function deposit(address yieldToken, uint256 amount, address recipient) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false);
            // #endif
        }

        _checkEnabled(yieldToken);

        // Check to assure that the yieldToken has not experienced a sudden unexpected loss. This prevents users from
        // being able to deposit funds and then have them siphoned once the price recovers.
        //
        // An example of this occurred with Yearn where a bug in a strategy reported a loss where there wasn't.
        // Later, the strategy was fixed and users who withdrew during that time lost funds.
        _checkLoss(yieldToken);

        _poke(recipient, yieldToken);
        _issueSharesForAmount(recipient, yieldToken, amount);
        _sync(yieldToken, amount, _uadd);

        TokenUtils.safeTransferFrom(yieldToken, msg.sender, address(this), amount);

        emit Deposit(msg.sender, yieldToken, amount, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function depositUnderlying(address yieldToken, uint256 amount, address recipient, bytes memory data) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false);
            // #endif
        }

        uint256 wrapped = _wrap(yieldToken, amount, data);

        _checkEnabled(yieldToken);

        // See note in deposit.
        _checkLoss(yieldToken);

        _poke(recipient, yieldToken);
        _issueSharesForAmount(recipient, yieldToken, wrapped);
        _sync(yieldToken, wrapped, _uadd);

        emit Deposit(msg.sender, yieldToken, wrapped, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function withdraw(address yieldToken, uint256 shares, address recipient) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false, "UnsupportedToken()");
            // #endif
        }

        _poke(msg.sender);

        uint256 amount = _convertSharesToYieldTokens(yieldToken, shares);

        _burnShares(msg.sender, yieldToken, shares);
        _sync(yieldToken, amount, _usub);

        _validate(msg.sender);

        TokenUtils.safeTransfer(yieldToken, recipient, amount);

        emit Withdraw(msg.sender, yieldToken, shares, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function withdrawFrom(address owner, address token, uint256 shares, address recipient) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(token)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(token);
            // #else
            require(false, "UnsupportedToken()");
            // #endif
        }

        _poke(owner);

        uint256 amount = _convertSharesToYieldTokens(token, shares);

        _burnShares(owner, token, shares);
        _sync(token, amount, _usub);

        _validate(owner);

        _decreaseWithdrawAllowance(owner, msg.sender, amount);

        TokenUtils.safeTransfer(token, recipient, amount);

        emit Withdraw(owner, token, shares, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function withdrawUnderlying(address yieldToken, uint256 shares, address recipient, bytes memory data) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false, "UnsupportedToken()");
            // #endif
        }

        // See note in deposit.
        _checkLoss(yieldToken);

        _poke(msg.sender);

        uint256 amount = _convertSharesToYieldTokens(yieldToken, shares);

        _burnShares(msg.sender, yieldToken, shares);
        _sync(yieldToken, amount, _usub);

        _validate(msg.sender);

        _unwrap(yieldToken, amount, recipient, data);

        emit Withdraw(msg.sender, yieldToken, shares, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function withdrawUnderlyingFrom(address owner, address token, uint256 shares, address recipient, bytes memory data) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(token)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(token);
            // #else
            require(false, "UnsupportedToken()");
            // #endif
        }

        // See note in deposit.
        _checkLoss(token);

        _poke(owner);

        uint256 amount = _convertSharesToYieldTokens(token, shares);

        _burnShares(owner, token, shares);
        _sync(token, amount, _usub);

        _validate(owner);

        _decreaseWithdrawAllowance(owner, msg.sender, amount);

        _unwrap(token, amount, recipient, data);

        emit Withdraw(msg.sender, token, shares, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function mint(uint256 amount, address recipient) external override lock {
        onlyWhitelisted();
        _checkMintingLimit(amount);

        _poke(msg.sender);
        _updateDebt(msg.sender, SafeCast.toInt256(amount), _sadd);
        _validate(msg.sender);

        _mintingLimiter.decrease(amount);

        TokenUtils.safeMint(debtToken, recipient, amount);

        emit Mint(msg.sender, amount, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function mintFrom(address owner, uint256 amount, address recipient) external override lock {
        onlyWhitelisted();
        _poke(owner);
        _updateDebt(owner, SafeCast.toInt256(amount), _sadd);
        _validate(owner);

        _decreaseMintAllowance(owner, msg.sender, amount);

        TokenUtils.safeMint(debtToken, recipient, amount);

        emit Mint(owner, amount, recipient);
    }

    /// @inheritdoc IAlchemistV2Permit
    function permitMint(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public override {
        onlyWhitelisted();
        if (block.timestamp > deadline) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert SignatureDeadlineExceeded();
            // #else
            require(false);
            // #endif
        }

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR(),
                keccak256(abi.encode(PERMIT_MINT_TYPEHASH, owner, spender, value, nonces[owner]++, deadline))
            )
        );

        address recovered = ecrecover(digest, v, r, s);
        if (owner != recovered) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert InvalidSignature();
            // #else
            require(false, "InvalidSignature()");
            // #endif
        }

        _approveMint(owner, spender, value);
    }

    function permitWithdraw(
        address owner,
        address withdrawer,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public override {
        onlyWhitelisted();
        if (block.timestamp > deadline) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert SignatureDeadlineExceeded();
            // #else
            require(false, "SignatureDeadlineExceeded()");
            // #endif
        }

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR(),
                keccak256(abi.encode(PERMIT_WITHDRAW_TYPEHASH, owner, withdrawer, value, nonces[owner]++, deadline))
            )
        );

        address recovered = ecrecover(digest, v, r, s);
        if (owner != recovered) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert InvalidSignature();
            // #else
            require(false, "InvalidSignature()");
            // #endif
        }

        _approveWithdraw(owner, withdrawer, value);
    }

    /// @inheritdoc IAlchemistV2Actions
    function burn(uint256 amount, address recipient) external override lock {
        onlyWhitelisted();
        _poke(recipient);
        _updateDebt(recipient, SafeCast.toInt256(amount), _ssub);

        TokenUtils.safeBurnFrom(debtToken, msg.sender, amount);

        AlchemicTokenV2(debtToken).lowerHasMinted(amount);

        emit Burn(msg.sender, amount, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function repay(address underlyingToken, uint256 amount, address recipient) external override lock {
        onlyWhitelisted();
        if (!_supportedUnderlyingTokens.contains(underlyingToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(underlyingToken);
            // #else
            require(false);
            // #endif
        }

        if (!_underlyingTokens[underlyingToken].enabled) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert TokenDisabled(underlyingToken);
            // #else
            require(false);
            // #endif
        }

        uint256 repayLimit = _underlyingTokens[underlyingToken].repayLimiter.get();
        if (amount > repayLimit) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert RepayLimitExceeded(underlyingToken, amount, repayLimit);
            // #else
            require(false);
            // #endif
        }

        _poke(recipient);
        _updateDebt(recipient, SafeCast.toInt256(_normalizeUnderlying(underlyingToken, amount)), _ssub);
        _underlyingTokens[underlyingToken].repayLimiter.decrease(amount);

        TokenUtils.safeTransferFrom(underlyingToken, msg.sender, transmuter, amount);

        IERC20TokenReceiver(transmuter).onERC20Received(underlyingToken, amount);

        if (_accounts[recipient].debt < 0) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert IllegalState("Debt overpaid");
            // #else
            require(false);
            // #endif
        }

        emit Repay(msg.sender, underlyingToken, amount, recipient);
    }

    /// @inheritdoc IAlchemistV2Actions
    function liquidate(address yieldToken, uint256 shares, bytes memory params) external override lock {
        onlyWhitelisted();
        if (!_supportedYieldTokens.contains(yieldToken)) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert UnsupportedToken(yieldToken);
            // #else
            require(false);
            // #endif
        }

        address underlyingToken = _yieldTokens[yieldToken].underlyingToken;

        _checkEnabled(yieldToken);
        _checkLoss(yieldToken);

        uint256 amountYield = _convertSharesToYieldTokens(yieldToken, shares);
        uint256 amountUnderlying = _unwrap(yieldToken, amountYield, address(this), params);

        uint256 liquidationLimit = _underlyingTokens[underlyingToken].liquidationLimiter.get();
        if (amountUnderlying > liquidationLimit) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert LiquidationLimitExceeded(underlyingToken, amountUnderlying, liquidationLimit);
            // #else
            require(false);
            // #endif
        }

        // Enter the critical section where modifications will occur. After this point, external calls should
        // be limited solely to read only functions so that external contracts cannot mutate state. This then
        // assures that repeated calls to any read only function will return the same result when called repeatedly.
        //
        // For liquidations a validation check is not required since it is asserted that the debt will always be decreased.
        _poke(msg.sender, yieldToken);

        _burnShares(msg.sender, yieldToken, shares);
        _updateDebt(
            msg.sender,
            SafeCast.toInt256(_normalizeUnderlying(_yieldTokens[yieldToken].underlyingToken, amountUnderlying)),
            _ssub
        );
        _underlyingTokens[underlyingToken].liquidationLimiter.decrease(amountUnderlying);
        _sync(yieldToken, amountYield, _usub);

        // Exit out of the critical section. Beyond this point, the state is committed and unsafe external calls can occur.

        TokenUtils.safeTransfer(underlyingToken, transmuter, amountUnderlying);

        IERC20TokenReceiver(transmuter).onERC20Received(underlyingToken, amountUnderlying);

        emit Liquidate(msg.sender, yieldToken, shares);
    }

    /// @inheritdoc IAlchemistV2Actions
    function donate(address yieldToken, uint256 amount) external override lock {
        onlyWhitelisted();
        _poke(msg.sender);

        _yieldTokens[yieldToken].accruedWeight += amount * 1e18 / (_yieldTokens[yieldToken].totalShares - _accounts[msg.sender].balances[yieldToken]);
        _accounts[msg.sender].lastAccruedWeights[yieldToken] = _yieldTokens[yieldToken].accruedWeight;

        TokenUtils.safeBurnFrom(debtToken, msg.sender, amount);

        emit Donate(msg.sender, yieldToken, amount);
    }

    /// @inheritdoc IAlchemistV2Actions
    function harvest(address yieldToken, bytes memory params) external override lock onlyKeeper {
        uint256 currentValue  = _convertYieldTokensToUnderlying(yieldToken, _yieldTokens[yieldToken].balance);
        uint256 expectedValue = _yieldTokens[yieldToken].expectedValue;
        if (currentValue <= expectedValue) {
            return;
        }

        uint256 harvestable = _convertUnderlyingTokensToYield(yieldToken, currentValue - expectedValue);
        if (harvestable == 0) {
            return;
        }

        address underlyingToken = _yieldTokens[yieldToken].underlyingToken;
        uint256 unwrapped = _unwrap(yieldToken, harvestable, address(this), params);

        uint256 fee = unwrapped * protocolFee / MAX_BPS;
        uint256 distributeAmt = unwrapped - fee;
        uint256 normDistributeAmt = _normalizeUnderlying(underlyingToken, distributeAmt);

        _distribute(yieldToken, normDistributeAmt);
        _yieldTokens[yieldToken].balance -= harvestable;

        TokenUtils.safeTransfer(underlyingToken, protocolFeeReceiver, fee);
        TokenUtils.safeTransfer(underlyingToken, transmuter, distributeAmt);

        IERC20TokenReceiver(transmuter).onERC20Received(underlyingToken, distributeAmt);

        emit Harvest(yieldToken, params);
    }

    /// @dev Checks if a yield-token and its underlying-token are both enabled.
    ///
    /// @notice Reverts if either the yield-token or its underlying-token are disabled.
    ///
    /// @param yieldToken The yield-token to check.
    function _checkEnabled(address yieldToken) internal view {
        if (!_yieldTokens[yieldToken].enabled) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert TokenDisabled(yieldToken);
            // #else
            require(false);
            // #endif
        }

        address underlyingToken = _yieldTokens[yieldToken].underlyingToken;
        if (!_underlyingTokens[underlyingToken].enabled) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert TokenDisabled(underlyingToken);
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Checks if an amount can be minted.
    ///
    /// @notice Reverts if minting `amount` would breach the minting limit.
    ///
    /// @param amount The amount to check.
    function _checkMintingLimit(uint256 amount) internal view {
        uint256 limit = _mintingLimiter.get();
        if (amount > limit) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert MintingLimitExceeded(amount, limit);
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Checks if there has been a loss of value in a yield-token.
    ///
    /// @param yieldToken The yield-token to check.
    function _checkLoss(address yieldToken) internal view {
        uint256 loss = _loss(yieldToken);
        uint256 maximumLoss = _yieldTokens[yieldToken].maximumLoss;
        if (loss > maximumLoss) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert LossExceeded(yieldToken, loss, maximumLoss);
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Sync the state of the yield-token in the Alchemist.
    ///
    /// @param yieldToken The yield-token to sync.
    /// @param delta The marginal amount of yield-tokens to add or subtract from the Alchemist's balance.
    /// @param op    The mathematical operation to peform as part of the sync (ex. add(balance, delta)).
    function _sync(
        address yieldToken,
        uint256 delta,
        function(uint256, uint256) internal pure returns (uint256) op
    ) internal {
        uint256 currentBalance = _yieldTokens[yieldToken].balance;
        uint256 currentExpectedValue = _yieldTokens[yieldToken].expectedValue;
        uint256 deltaValue = _convertYieldTokensToUnderlying(yieldToken, delta);
        uint256 newExpectedValue = op(currentExpectedValue, deltaValue);
        _yieldTokens[yieldToken].balance = op(currentBalance, delta);
        _yieldTokens[yieldToken].expectedValue = newExpectedValue;

        uint256 maximumExpectedValue = _yieldTokens[yieldToken].maximumExpectedValue;
        if (newExpectedValue > maximumExpectedValue) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert ExpectedValueExceeded(yieldToken, delta, _yieldTokens[yieldToken].maximumExpectedValue);
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Calculates the amount of loss that a yield-token has suffered.
    ///
    /// @notice A yield-token could experience a loss by being exploited and having underlying-tokens stolen.
    ///
    /// @param yieldToken The yield-token to check.
    ///
    /// @return The total loss experienced by the yield-token, in units of underlying-token.
    function _loss(address yieldToken) internal view returns (uint256) {
        uint256 currentValue = _convertYieldTokensToUnderlying(yieldToken, _yieldTokens[yieldToken].balance);
        uint256 expectedValue = _yieldTokens[yieldToken].expectedValue;
        return expectedValue > currentValue ? (expectedValue - currentValue) * 1e4 / currentValue : 0;
    }

    /// @dev Gives credit / yield to the CDPs holding a given yield-token in the Alchemist.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param amount The amount of underlying-token to distribute as credit / yield.
    function _distribute(address yieldToken, uint256 amount) internal {
        _yieldTokens[yieldToken].accruedWeight += amount * 1e18 / _yieldTokens[yieldToken].totalShares;
    }

    /// @dev Deposits an underlying-token into a yield-token.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param amount The amount of underlying-token to deposit.
    /// @param data   A packet of bytes sent to the `wrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `deposit` function (or similar) of the target yield-token.
    ///
    /// @return The amount of yield-tokens received.
    function _wrap(address yieldToken, uint256 amount, bytes memory data) internal returns (uint256) {
        ITokenAdapter adapter = ITokenAdapter(_yieldTokens[yieldToken].adapter);
        address underlyingToken = _yieldTokens[yieldToken].underlyingToken;
        TokenUtils.safeTransferFrom(underlyingToken, msg.sender, address(this), amount);
        return adapter.wrap(amount, address(this), data);
    }

    /// @dev Deposits an underlying-token into a yield-token.
    ///
    /// @param yieldToken        The target yield-token.
    /// @param amount       The amount of underlying-token to deposit.
    /// @param recipient    The account to receive the resulting underlying-tokens.
    /// @param data         A packet of bytes sent to the `unwrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    ///
    /// @return The amount of underlying-tokens received.
    function _unwrap(address yieldToken, uint256 amount, address recipient, bytes memory data) internal returns (uint256) {
        ITokenAdapter adapter = ITokenAdapter(_yieldTokens[yieldToken].adapter);
        return adapter.unwrap(amount, recipient, data);
    }

    /// @dev Updates an account.
    ///
    /// @param owner the address that owns the account to update.
    function _poke(address owner) internal {
        Sets.AddressSet storage depositedTokens = _accounts[owner].depositedTokens;
        for (uint256 i = 0; i < depositedTokens.values.length; i++) {
            _poke(owner, depositedTokens.values[i]);
        }
    }

    /// @dev Updates an account.
    ///
    /// @param owner the address that owns the account.
    /// @param yieldToken the address of the yield-token to update.
    function _poke(address owner, address yieldToken) internal {
        uint256 currentAccruedWeight = _yieldTokens[yieldToken].accruedWeight;
        uint256 lastAccruedWeight = _accounts[owner].lastAccruedWeights[yieldToken];

        if (currentAccruedWeight == lastAccruedWeight) {
            return;
        }

        uint256 balance = _accounts[owner].balances[yieldToken];
        uint256 unrealizedCredit = (currentAccruedWeight - lastAccruedWeight) * balance / 1e18;

        _accounts[owner].debt -= SafeCast.toInt256(unrealizedCredit);
        _accounts[owner].lastAccruedWeights[yieldToken] = currentAccruedWeight;
    }

    /// @dev Update the debt of a target CDP.
    ///
    /// @param owner     The owner of the CDP.
    /// @param delta     The amount to add to or subract from the current debt.
    /// @param operation The mathematical operaiont (add/subtract) to perform for the update.
    function _updateDebt(
        address owner,
        int256 delta,
        function(int256, int256) internal pure returns (int256) operation
    ) internal {
        Account storage account = _accounts[owner];
        account.debt = operation(account.debt, delta);
    }

    /// @dev Set the mintAllowance for a given spender on a CDP.
    ///
    /// @param owner   The owner of the CDP.
    /// @param spender The account that will be allowed to mint debt from the CDP.
    /// @param amount  The amount of debt that the spender will be allowed to mint.
    function _approveMint(address owner, address spender, uint256 amount) internal {
        Account storage account = _accounts[owner];
        account.mintAllowances[spender] = amount;
        emit ApproveMint(owner, spender, amount);
    }

    /// @dev Decrease the mintAllowance for a given spender on a CDP.
    ///
    /// @param owner   The owner of the CDP.
    /// @param spender The account that will be allowed to mint debt from the CDP.
    /// @param delta   The amount that the spender's allowance will be reduced.
    function _decreaseMintAllowance(
        address owner,
        address spender,
        uint256 delta
    ) internal {
        Account storage account = _accounts[owner];
        account.mintAllowances[spender] -= delta;
    }

    /// @dev Set the withdrawAllowance for a given spender on a CDP.
    ///
    /// @param owner   The owner of the CDP.
    /// @param withdrawer The account that will be allowed to withdraw collateral from the CDP.
    /// @param amount  The amount of collateral that the withdrawer will be allowed to withdraw.
    function _approveWithdraw(address owner, address withdrawer, uint256 amount) internal {
        Account storage account = _accounts[owner];
        account.withdrawAllowances[withdrawer] = amount;
        emit ApproveWithdraw(owner, withdrawer, amount);
    }

    /// @dev Decrease the mintAllowance for a given spender on a CDP.
    ///
    /// @param owner   The owner of the CDP.
    /// @param withdrawer The account that will be allowed to mint debt from the CDP.
    /// @param delta   The amount that the spender's allowance will be reduced.
    function _decreaseWithdrawAllowance(
        address owner,
        address withdrawer,
        uint256 delta
    ) internal {
        Account storage account = _accounts[owner];
        account.withdrawAllowances[withdrawer] -= delta;
    }

    /// @dev Check that a target CDP has a health collateralization ratio.
    ///
    /// @notice Reverts if the collateralization ratio is unhealthy.
    ///
    /// @param owner The owner of the CDP.
    function _validate(address owner) internal view {
        int256 debt = _accounts[owner].debt;
        if (debt <= 0) {
            return;
        }

        uint256 totalValue = _totalValue(owner) ;
        uint256 collateralization = totalValue * 1e18 / uint256(debt);

        if (collateralization < minimumCollateralization) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert Undercollateralized();
            // #else
            require(false);
            // #endif
        }
    }

    /// @dev Gets the total value of a target CDP.
    ///
    /// @param owner The owner of the CDP.
    ///
    /// @return The total number of underlying-tokens from all the yield-tokens held by the CDP.
    function _totalValue(address owner) internal view returns (uint256) {
        uint256 totalValue = 0;
        Sets.AddressSet storage activeTokens = _accounts[owner].depositedTokens;
        for (uint256 i = 0; i < activeTokens.values.length; i++) {
            address yieldToken = activeTokens.values[i];
            address underlyingToken = _yieldTokens[yieldToken].underlyingToken;
            uint256 value = _convertSharesToUnderlyingTokens(yieldToken, _accounts[owner].balances[yieldToken]);
            uint256 normalized = _normalizeUnderlying(underlyingToken, value);
            totalValue += normalized;
        }
        return totalValue;
    }

    /// @dev Issues proporitonal shares to a CDP for an amount of yield-tokens.
    ///
    /// @param owner  The owner of the CDP.
    /// @param yieldToken  The yield-token used to issue shares.
    /// @param amount The amount of yield-tokens that are being used to issue shares.
    ///
    /// @return The amount of shares issued.
    function _issueSharesForAmount(address owner, address yieldToken, uint256 amount) internal returns (uint256) {
        uint256 shares = _convertYieldTokensToShares(yieldToken, amount);

        assert(shares > 0);

        if (_accounts[owner].balances[yieldToken] == 0) {
            _accounts[owner].depositedTokens.add(yieldToken);
        }

        _accounts[owner].balances[yieldToken] += shares;
        _yieldTokens[yieldToken].totalShares += shares;

        return shares;
    }

    /// @dev Burns shares in a CDP.
    ///
    /// @param owner  The owner of the CDP.
    /// @param yieldToken  The yield-token used to issue the shares.
    /// @param shares The amount of shares to be burned.
    function _burnShares(address owner, address yieldToken, uint256 shares) internal {
        _accounts[owner].balances[yieldToken] -= shares;
        _yieldTokens[yieldToken].totalShares -= shares;

        if (_accounts[owner].balances[yieldToken] == 0) {
            _accounts[owner].depositedTokens.remove(yieldToken);
        }
    }

    /// @dev Converts a given amount of yield-tokens into shares.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param amount The amount of target yield-token.
    ///
    /// @return The number of shares represented by the given amount of yield-token.
    function _convertYieldTokensToShares(address yieldToken, uint256 amount) internal view returns (uint256) {
        if (_yieldTokens[yieldToken].totalShares == 0) {
            return amount;
        }
        return amount * _yieldTokens[yieldToken].totalShares / _yieldTokens[yieldToken].balance;
    }

    /// @dev Converts a given amount of shares into yield-tokens.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param shares The amount of yield-token shares.
    ///
    /// @return The number of yield-tokens represented by the given amount of shares.
    function _convertSharesToYieldTokens(address yieldToken, uint256 shares) internal view returns (uint256) {
        uint256 totalShares = _yieldTokens[yieldToken].totalShares;
        if (totalShares == 0) {
            return shares;
        }
        return shares * _yieldTokens[yieldToken].balance / totalShares;
    }

    /// @dev Converts a given amount of shares into underlying-tokens.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param shares The amount of yield-token shares.
    ///
    /// @return The number of underlying-tokens represented by the given amount of shares.
    function _convertSharesToUnderlyingTokens(address yieldToken, uint256 shares) internal view returns (uint256) {
        uint256 amount = _convertSharesToYieldTokens(yieldToken, shares);
        return _convertYieldTokensToUnderlying(yieldToken, amount);
    }

    /// @dev Converts a given amount of yield-tokens into underlying-tokens.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param amount The amount of yield-tokens.
    ///
    /// @return The number of underlying-tokens represented by the given amount of yield-tokens.
    function _convertYieldTokensToUnderlying(address yieldToken, uint256 amount) internal view returns (uint256) {
        uint256 price = ITokenAdapter(_yieldTokens[yieldToken].adapter).price();
        return amount * price / 10** _yieldTokens[yieldToken].decimals;
    }

    /// @dev Converts a given amount of underlying-tokens into yield-tokens.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param amount The amount of underlying-tokens (redeemable for yield-token).
    ///
    /// @return The number of yield-tokens represented by the given amount of underlying-tokens.
    function _convertUnderlyingTokensToYield(address yieldToken, uint256 amount) internal view returns (uint256) {
        uint256 price = ITokenAdapter(_yieldTokens[yieldToken].adapter).price();
        return amount * 10** _yieldTokens[yieldToken].decimals / price;
    }

    /// @dev Normalize the number of decimals representing a given amount.
    ///
    /// @param underlyingToken  The target yield-token.
    /// @param amount The amount of the target yield-token.
    ///
    /// @return A decimal-normalized version of `amount`, for use in calculations with other normalized yield-token amounts.
    function _normalizeUnderlying(address underlyingToken, uint256 amount) internal view returns (uint256) {
        return amount * _underlyingTokens[underlyingToken].conversionFactor;
    }

    /// @dev Adds two unsigned 256 bit integers together and returns the result.
    ///
    /// @dev This operation is checked and will fail if the result overflows.
    ///
    /// @param x the first operand.
    /// @param y the second operand.
    ///
    /// @return z the result.
    function _uadd(uint256 x, uint256 y) internal pure returns (uint256 z) { z = x + y; }

    /// @dev Subtracts two unsigned 256 bit integers together and returns the result.
    ///
    /// @dev This operation is checked and will fail if the result overflows.
    ///
    /// @param x the first operand.
    /// @param y the second operand.
    ///
    /// @return z the result.
    function _usub(uint256 x, uint256 y) internal pure returns (uint256 z) { z = x - y; }

    /// @dev Adds two signed 256 bit integers together and returns the result.
    ///
    /// @dev This operation is checked and will fail if the result overflows.
    ///
    /// @param x the first operand.
    /// @param y the second operand.
    ///
    /// @return z the result.
    function _sadd(int256 x, int256 y) internal pure returns (int256 z) { z = x + y; }

    /// @dev Subtracts two signed 256 bit integers together and returns the result.
    ///
    /// @dev This operation is checked and will fail if the result overflows.
    ///
    /// @param x the first operand.
    /// @param y the second operand.
    ///
    /// @return z the result.
    function _ssub(int256 x, int256 y) internal pure returns (int256 z) { z = x - y; }
}","keccak256":"0x3ff8327e42e06379fe6d77a9357ccd3c0dbd1094ef45848e33396cb0711cd97f"},"contracts/ContractWhitelister.sol":{"content":"pragma solidity ^0.8.9;


abstract contract ContractWhitelister {
    // #if !(DISABLE_CUSTOM_ERRORS)
    /// @dev Thrown when a non-whitelisted contract attempts to access a whitelist-only function.
    error NotWhitelisted();
    /// @dev Thrown when anyone but a whitelist admin tries to call an admin function.
    error UnauthorizedER();
    // #endif

    /// @dev Emitted when a contract is added to the whitelist.
    ///
    /// @param account The account that was added to the whitelist.
    event AccountAdded(address account);

    /// @dev Emitted when a contract is removed from the whitelist.
    ///
    /// @param account The account that was removed from the whitelist.
    event AccountRemoved(address account);

    /// @dev Emitted when the whitelist is activated.
    event WhitelistEnabled();

    /// @dev Emitted when the whitelist is deactivated.
    event WhitelistDisabled();

    /// @dev Turns whitelist gate on or off.
    bool public whitelistActive;

    /// @dev A mapping of whitelisted contracts.
    mapping(address => bool) public whitelist;

    /// @dev When whitelist is active, only allow calls from whitelisted contracts to pass.
    function onlyWhitelisted() internal {
        if (whitelistActive && msg.sender != tx.origin && !whitelist[msg.sender]) {
            // #if !(DISABLE_CUSTOM_ERRORS)
            revert NotWhitelisted();
            // #else
            require(false);
            // #endif
        }
    }

    function onlyAdmin() internal virtual {
        require(false, "override onlyAdmin modifier");
    }

    /// @dev Adds an contract to the whitelist.
    ///
    /// @param account the contract to add to the whitelist.
    function addToWhitelist(address account) external {
        onlyAdmin();
        whitelist[account] = true;
        emit AccountAdded(account);
    }

    /// @dev Adds an contract to the whitelist.
    ///
    /// @param account the contract to remove from the whitelist.
    function removeFromWhitelist(address account) external {
        onlyAdmin();
        whitelist[account] = false;
        emit AccountRemoved(account);
    }

    /// @dev Activates the whitelist.
    ///
    function enableWhitelist() external {
        onlyAdmin();
        whitelistActive = true;
        emit WhitelistEnabled();
    }

    /// @dev De-activates the whitelist.
    ///
    function disableWhitelist() external {
        onlyAdmin();
        whitelistActive = false;
        emit WhitelistDisabled();
    }
}","keccak256":"0x10d325fd135e10ad6561ce98be336b64b4f7115feb325376a03bc4a026ff2c58"},"contracts/TransmuterV2.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity =0.8.9;

import "./interfaces/IDetailedERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./libraries/FixedPointMath.sol";
import "./libraries/LiquidityMath.sol";
import "./libraries/SafeCast.sol";
import "./libraries/Tick.sol";
import "./interfaces/transmuter/ITransmuterV2.sol";
import "./interfaces/transmuter/ITransmuterBuffer.sol";
import "./ContractWhitelister.sol";

/// @title TransmuterV2
///
/// @notice A contract which facilitates the exchange of synthetic assets for their underlying
//          asset. This contract guarantees that synthetic assets are exchanged exactly 1:1
//          for the underlying asset.
contract TransmuterV2 is ITransmuterV2, ReentrancyGuard, ContractWhitelister, AccessControl {
  using SafeERC20 for IDetailedERC20;
  using SafeERC20 for ERC20Burnable;
  using FixedPointMath for FixedPointMath.Number;
  using Tick for Tick.Cache;

  // #if !(DISABLE_CUSTOM_ERRORS)
  /// @dev Thrown when anyone but a whitelist admin tries to call an admin function.
  error Unauthorized();
  // #endif

  struct Account {
    // The total number of unexchanged tokens that an account has deposited into the system
    uint256 unexchangedBalance;
    // The total number of exchanged tokens that an account has had credited
    uint256 exchangedBalance;
    // The tick that the account has had their deposit associated in
    uint256 occupiedTick;
  }

  struct UpdateAccountParams {
    // The owner address whose account will be modified
    address owner;
    // The amount to change the account's unexchanged balance by
    int256 unexchangedDelta;
    // The amount to change the account's exchanged balance by
    int256 exchangedDelta;
  }

  struct ExchangeCache {
    // The total number of unexchanged tokens that exist at the start of the exchange call
    uint256 totalUnexchanged;
    // The tick which has been satisfied up to at the start of the exchange call
    uint256 satisfiedTick;
    // The head of the active ticks queue at the start of the exchange call
    uint256 ticksHead;
  }

  struct ExchangeState {
    // The position in the buffer of current tick which is being examined
    uint256 examineTick;
    // The total number of unexchanged tokens that currently exist in the system for the current distribution step
    uint256 totalUnexchanged;
    // The tick which has been satisfied up to, inclusive
    uint256 satisfiedTick;
    // The amount of tokens to distribute for the current step
    uint256 distributeAmount;
    // The accumulated weight to write at the new tick after the exchange is completed
    FixedPointMath.Number accumulatedWeight;
    // Reserved for the maximum weight of the current distribution step
    FixedPointMath.Number maximumWeight;
    // Reserved for the dusted weight of the current distribution step
    FixedPointMath.Number dustedWeight;
  }

  struct UpdateAccountCache {
    // The total number of unexchanged tokens that the account held at the start of the update call
    uint256 unexchangedBalance;
    // The total number of exchanged tokens that the account held at the start of the update call
    uint256 exchangedBalance;
    // The tick that the account's deposit occupies at the start of the update call
    uint256 occupiedTick;
    // The total number of unexchanged tokens that exist at the start of the update call
    uint256 totalUnexchanged;
    // The current tick that is being written to
    uint256 currentTick;
  }

  struct UpdateAccountState {
    // The updated unexchanged balance of the account being updated
    uint256 unexchangedBalance;
    // The updated exchanged balance of the account being updated
    uint256 exchangedBalance;
    // The updated total unexchanged balance
    uint256 totalUnexchanged;
  }

  address public constant ZERO_ADDRESS = address(0);

  /// @dev The identifier of the role which maintains other roles.
  bytes32 public constant ADMIN = keccak256("ADMIN");

  /// @dev The identitifer of the sentinel role
  bytes32 public constant SENTINEL = keccak256("SENTINEL");

  /// @dev the synthetic token to be transmuted
  address public syntheticToken;

  /// @dev the underlying token to be recieved
  IDetailedERC20 public underlyingToken;

  /// @dev The total amount of unexchanged tokens which are held by all accounts.
  uint256 public totalUnexchanged;

  /// @dev The total amount of tokens which are in the auxiliary buffer.
  uint256 public totalBuffered;

  /// @dev A mapping specifying all of the accounts.
  mapping(address => Account) private accounts;

  // @dev The tick buffer which stores all of the tick information along with the tick that is
  //      currently being written to. The "current" tick is the tick at the buffer write position.
  Tick.Cache private ticks;

  // The tick which has been satisfied up to, inclusive.
  uint256 private satisfiedTick;

  /// @dev contract admin address
  address public admin;

  /// @dev contract pending admin address
  address public pendingAdmin;

  /// @dev contract pause state
  bool public isPaused;

  /// @dev the source of the exchanged collateral
  address public buffer;

  event AdminUpdated(address admin);

  event PendingAdminUpdated(address pendingAdmin);

  event PauseUpdated(bool isPaused);

  constructor(
    address _syntheticToken,
    IDetailedERC20 _underlyingToken,
    address _buffer
  ) ContractWhitelister() {
    _setupRole(ADMIN, msg.sender);
    _setRoleAdmin(ADMIN, ADMIN);
    _setRoleAdmin(SENTINEL, ADMIN);

    syntheticToken = _syntheticToken;
    underlyingToken = _underlyingToken;
    buffer = _buffer;
    // Push a blank tick to function as a sentinel value in the active ticks queue.
    ticks.next();

    admin = msg.sender;
    isPaused = false;
    whitelistActive = true;
  }

  /// @dev A modifier which checks if caller is an alchemist.
  modifier onlyBuffer() {
    require(msg.sender == buffer, "Transmuter: caller is not the collateral source");
    _;
  }

  /// @dev A modifier which checks if caller is a sentinel.
  modifier onlySentinel() {
    require(hasRole(SENTINEL, msg.sender), "Transmuter: caller is not a Sentinel");
    _;
  }

  /// @dev A modifier which checks if caller is a sentinel or admin.
  modifier onlySentinelOrAdmin() {
    require(hasRole(SENTINEL, msg.sender), "Transmuter: caller must be Sentinel or Admin");
    _;
  }

  /// @dev A modifier which checks if caller is a sentinel.
  modifier notPaused() {
    require(!isPaused, "Transmuter: paused");
    _;
  }

  function onlyAdmin() internal override {
    require(msg.sender == admin, "Transmuter: caller is not admin");
  }

  function setSentinel(address _newSentinel) external {
    onlyAdmin();
    _setupRole(SENTINEL, _newSentinel);
  }

  function setCollateralSource(address _newCollateralSource) external {
    onlyAdmin();
    buffer = _newCollateralSource;
  }

  function setPause(bool pauseState) external onlySentinelOrAdmin {
    isPaused = pauseState;
    emit PauseUpdated(isPaused);
  }

  /// @dev Sets the pending admin.
  ///
  /// This function reverts if the new pending admin is the zero address or the caller is not the current
  /// admin. This is to prevent the contract admin being set to the zero address which would deadlock
  /// privileged contract functionality.
  ///
  /// @param _pendingAdmin the new pending admin.
  function setPendingAdmin(address _pendingAdmin) external {
    onlyAdmin();
    require(_pendingAdmin != ZERO_ADDRESS, "Transmuter: admin cannot be zero address");

    pendingAdmin = _pendingAdmin;

    emit PendingAdminUpdated(_pendingAdmin);
  }

  /// @dev Accepts the role as admin.
  ///
  /// This function reverts if the caller is not the new pending admin.
  function acceptAdmin() external {
    require(msg.sender == pendingAdmin, "Transmuter: not currently pending admin");
    address _pendingAdmin = pendingAdmin;
    admin = _pendingAdmin;

    emit AdminUpdated(_pendingAdmin);
  }

  /// @inheritdoc ITransmuterV2
  function deposit(uint256 amount, address owner) external override nonReentrant {
    onlyWhitelisted();
    _updateAccount(UpdateAccountParams({owner: owner, unexchangedDelta: SafeCast.toInt256(amount), exchangedDelta: 0}));
    IDetailedERC20(syntheticToken).safeTransferFrom(msg.sender, address(this), amount);
  }

  /// @inheritdoc ITransmuterV2
  function withdraw(uint256 amount, address recipient) external override nonReentrant {
    onlyWhitelisted();
    _updateAccount(
      UpdateAccountParams({owner: msg.sender, unexchangedDelta: -SafeCast.toInt256(amount), exchangedDelta: 0})
    );
    IDetailedERC20(syntheticToken).safeTransfer(recipient, amount);
  }

  /// @inheritdoc ITransmuterV2
  function claim(
    uint256 amount,
    address recipient
  ) external override nonReentrant {
    onlyWhitelisted();
    _updateAccount(
      UpdateAccountParams({owner: msg.sender, unexchangedDelta: 0, exchangedDelta: -SafeCast.toInt256(amount)})
    );
    ERC20Burnable(syntheticToken).burn(amount);
    ITransmuterBuffer(buffer).withdraw(underlyingToken, amount, msg.sender);
  }

  /// @inheritdoc ITransmuterV2
  function exchange(uint256 amount) external override nonReentrant onlyBuffer notPaused {
    if (totalUnexchanged == 0) {
      totalBuffered += amount;
      return;
    }

    // Push a storage reference to the current tick.
    Tick.Info storage current = ticks.current();

    ExchangeCache memory cache = ExchangeCache({
      totalUnexchanged: totalUnexchanged,
      satisfiedTick: satisfiedTick,
      ticksHead: ticks.head
    });

    ExchangeState memory state = ExchangeState({
      examineTick: cache.ticksHead,
      totalUnexchanged: cache.totalUnexchanged,
      satisfiedTick: cache.satisfiedTick,
      distributeAmount: amount,
      accumulatedWeight: current.accumulatedWeight,
      maximumWeight: FixedPointMath.encode(0),
      dustedWeight: FixedPointMath.encode(0)
    });

    // Distribute the buffered tokens as part of the exchange.
    state.distributeAmount += totalBuffered;
    totalBuffered = 0;

    // Push a storage reference to the next tick to write to.
    Tick.Info storage next = ticks.next();

    // Only iterate through the active ticks queue when it is not empty.
    while (state.examineTick != 0) {
      // Check if there is anything left to distribute.
      if (state.distributeAmount == 0) {
        break;
      }

      Tick.Info storage examineTickData = ticks.get(state.examineTick);

      // Add the weight for the distribution step to the accumulated weight.
      state.accumulatedWeight = state.accumulatedWeight.add(
        FixedPointMath.rational(state.distributeAmount, state.totalUnexchanged)
      );

      // Clear the distribute amount.
      state.distributeAmount = 0;

      // Calculate the current maximum weight in the system.
      state.maximumWeight = state.accumulatedWeight.sub(examineTickData.accumulatedWeight);

      // Check if there exists at least one account which is completely satisfied..
      if (state.maximumWeight.n < FixedPointMath.ONE) {
        break;
      }

      // Calculate how much weight of the distributed weight is dust.
      state.dustedWeight = FixedPointMath.Number(state.maximumWeight.n - FixedPointMath.ONE);

      // Calculate how many tokens to distribute in the next step. These are tokens from any tokens which
      // were over allocated to accounts occupying the tick with the maximum weight.
      state.distributeAmount = LiquidityMath.calculateProportion(examineTickData.totalBalance, state.dustedWeight);

      // Remove the tokens which were completely exchanged from the total unexchanged balance.
      state.totalUnexchanged -= examineTickData.totalBalance;

      // Write that all ticks up to and including the examined tick have been satisfied.
      state.satisfiedTick = state.examineTick;

      // Visit the next active tick. This is equivalent to popping the head of the active ticks queue.
      state.examineTick = examineTickData.next;
    }

    // Write the accumulated weight to the next tick.
    next.accumulatedWeight = state.accumulatedWeight;

    if (cache.totalUnexchanged != state.totalUnexchanged) {
      totalUnexchanged = state.totalUnexchanged;
    }

    if (cache.satisfiedTick != state.satisfiedTick) {
      satisfiedTick = state.satisfiedTick;
    }

    if (cache.ticksHead != state.examineTick) {
      ticks.head = state.examineTick;
    }

    if (state.distributeAmount > 0) {
      totalBuffered += state.distributeAmount;
    }
  }

  /// @inheritdoc ITransmuterV2
  function getUnexchangedBalance(address owner) external view override returns (uint256 unexchangedBalance) {
    Account storage account = accounts[owner];

    if (account.occupiedTick <= satisfiedTick) {
      return 0;
    }

    unexchangedBalance = account.unexchangedBalance;

    uint256 exchanged = LiquidityMath.calculateProportion(
      unexchangedBalance,
      ticks.getWeight(account.occupiedTick, ticks.position)
    );

    unexchangedBalance -= exchanged;

    return unexchangedBalance;
  }

  /// @inheritdoc ITransmuterV2
  function getExchangedBalance(address owner) external view override returns (uint256 exchangedBalance) {
    Account storage account = accounts[owner];

    if (account.occupiedTick <= satisfiedTick) {
      exchangedBalance = account.exchangedBalance;
      exchangedBalance += account.unexchangedBalance;
      return exchangedBalance;
    }

    exchangedBalance = account.exchangedBalance;

    uint256 exchanged = LiquidityMath.calculateProportion(
      account.unexchangedBalance,
      ticks.getWeight(account.occupiedTick, ticks.position)
    );

    exchangedBalance += exchanged;

    return exchangedBalance;
  }

  /// @dev Updates an account.
  ///
  /// @param params The call parameters.
  function _updateAccount(UpdateAccountParams memory params) internal {
    Account storage account = accounts[params.owner];

    UpdateAccountCache memory cache = UpdateAccountCache({
      unexchangedBalance: account.unexchangedBalance,
      exchangedBalance: account.exchangedBalance,
      occupiedTick: account.occupiedTick,
      totalUnexchanged: totalUnexchanged,
      currentTick: ticks.position
    });

    UpdateAccountState memory state = UpdateAccountState({
      unexchangedBalance: cache.unexchangedBalance,
      exchangedBalance: cache.exchangedBalance,
      totalUnexchanged: cache.totalUnexchanged
    });

    // Updating an account is broken down into five steps:
    // 1). Synchronize the account if it previously occupied a satisfied tick
    // 2). Update the account balances to account for exchanged tokens, if any
    // 3). Apply the deltas to the account balances
    // 4). Update the previously occupied and or current tick's liquidity
    // 5). Commit changes to the account and global state when needed

    // Step one:
    // ---------
    // Check if the tick that the account was occupying previously was satisfied. If it was, we acknowledge
    // that all of the tokens were exchanged.
    if (state.unexchangedBalance > 0 && satisfiedTick >= cache.occupiedTick) {
      state.unexchangedBalance = 0;
      state.exchangedBalance += cache.unexchangedBalance;
    }

    // Step Two:
    // ---------
    // Calculate how many tokens were exchanged since the last update.
    if (state.unexchangedBalance > 0) {
      uint256 exchanged = LiquidityMath.calculateProportion(
        state.unexchangedBalance,
        ticks.getWeight(cache.occupiedTick, cache.currentTick)
      );

      state.totalUnexchanged -= exchanged;
      state.unexchangedBalance -= exchanged;
      state.exchangedBalance += exchanged;
    }

    // Step Three:
    // -----------
    // Apply the unexchanged and exchanged deltas to the state.
    state.totalUnexchanged = LiquidityMath.addDelta(state.totalUnexchanged, params.unexchangedDelta);
    state.unexchangedBalance = LiquidityMath.addDelta(state.unexchangedBalance, params.unexchangedDelta);
    state.exchangedBalance = LiquidityMath.addDelta(state.exchangedBalance, params.exchangedDelta);

    // Step Four:
    // ----------
    // The following is a truth table relating various values which in combinations specify which logic branches
    // need to be executed in order to update liquidity in the previously occupied and or current tick.
    //
    // Some states are not obtainable and are just discarded by setting all the branches to false.
    //
    // | P | C | M | Modify Liquidity | Add Liquidity | Subtract Liquidity |
    // |---|---|---|------------------|---------------|--------------------|
    // | F | F | F | F                | F             | F                  |
    // | F | F | T | F                | F             | F                  |
    // | F | T | F | F                | T             | F                  |
    // | F | T | T | F                | T             | F                  |
    // | T | F | F | F                | F             | T                  |
    // | T | F | T | F                | F             | T                  |
    // | T | T | F | T                | F             | F                  |
    // | T | T | T | F                | T             | T                  |
    //
    // | Branch             | Reduction |
    // |--------------------|-----------|
    // | Modify Liquidity   | PCM'      |
    // | Add Liquidity      | P'C + CM  |
    // | Subtract Liquidity | PC' + PM  |

    bool previouslyActive = cache.unexchangedBalance > 0;
    bool currentlyActive = state.unexchangedBalance > 0;
    bool migrate = cache.occupiedTick != cache.currentTick;

    bool modifyLiquidity = previouslyActive && currentlyActive && !migrate;

    if (modifyLiquidity) {
      Tick.Info storage tick = ticks.get(cache.occupiedTick);

      // Consolidate writes to save gas.
      uint256 totalBalance = tick.totalBalance;
      totalBalance -= cache.unexchangedBalance;
      totalBalance += state.unexchangedBalance;
      tick.totalBalance = totalBalance;
    } else {
      bool addLiquidity = (!previouslyActive && currentlyActive) || (currentlyActive && migrate);
      bool subLiquidity = (previouslyActive && !currentlyActive) || (previouslyActive && migrate);

      if (addLiquidity) {
        Tick.Info storage tick = ticks.get(cache.currentTick);

        if (tick.totalBalance == 0) {
          ticks.addLast(cache.currentTick);
        }

        tick.totalBalance += state.unexchangedBalance;
      }

      if (subLiquidity) {
        Tick.Info storage tick = ticks.get(cache.occupiedTick);
        tick.totalBalance -= cache.unexchangedBalance;

        if (tick.totalBalance == 0) {
          ticks.remove(cache.occupiedTick);
        }
      }
    }

    // Step Five:
    // ----------
    // Commit the changes to the account.
    if (cache.unexchangedBalance != state.unexchangedBalance) {
      account.unexchangedBalance = state.unexchangedBalance;
    }

    if (cache.exchangedBalance != state.exchangedBalance) {
      account.exchangedBalance = state.exchangedBalance;
    }

    if (cache.totalUnexchanged != state.totalUnexchanged) {
      totalUnexchanged = state.totalUnexchanged;
    }

    if (cache.occupiedTick != cache.currentTick) {
      account.occupiedTick = cache.currentTick;
    }
  }
}
