pragma solidity >=0.7.5;
pragma abicoder v2;

/// @title Multicall interface
/// @author Uniswap Labs
/// @notice Enables calling multiple methods in a single call to the contract
interface IMulticall {
    /// @notice Call multiple functions in the current contract and return the data from all of them if they all succeed
    /// @dev The `msg.value` should not be trusted for any method callable from multicall.
    /// @param data The encoded function data for each of the calls to make to this contract
    /// @return results The results from each of the calls passed in via data
    function multicall(bytes[] calldata data) external payable returns (bytes[] memory results);
}","keccak256":"0x84a0df8570f274bb4cbcc93a591ac18bd7b3a72b1f5fbab1b40aa1eb5d815fe4","license":"GPL-2.0-or-later"},"contracts/interfaces/ITokenAdapter.sol":{"content":"pragma solidity ^0.8.9;

/// @title  ITokenAdapter
/// @author Alchemix Finance
interface ITokenAdapter {
    /// @notice Gets the address of the yield-token used by the adapter.
    ///
    /// @return The address of the yield-token.
    function token() external view returns (address);

    /// @notice Gets the address of the underlying-token used by the adapter.
    ///
    /// @return The address of the underlying-token.
    function underlyingToken() external view returns (address);

    /// @notice Gets the current price of yield-token in underlying-tokens.
    ///
    /// @return The price (yield-tokens per underlying-token).
    function price() external view returns (uint256);

    /// @notice Deposits underlying-token into yield-token.
    ///
    /// @param amount    The amount of underlying-token to deposit.
    /// @param recipient The recipient of the resulting yield-tokens.
    /// @param data      A packet of bytes used by the adapter to complete the deposit action.  The format of these bytes is dependent on the `deposit` function (or similar) of the target yield-token.
    ///
    /// @return The amount of yield-token received.
    function wrap(uint256 amount, address recipient, bytes memory data) external returns (uint256);

    /// @notice Withdraws underlying-token from yield-token.
    ///
    /// @param amount    The amount of yield-tokens to redeem.
    /// @param recipient The recipient of the resulting underlying-tokens.
    /// @param data      A packet of bytes used by the adapter to complete the withdraw action.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    ///
    /// @return The amount of underlying-token received.
    function unwrap(uint256 amount, address recipient, bytes memory data) external returns (uint256);

    /// @notice Returns a default packet of bytes for use with the `wrap` function.
    ///
    /// @return A packet of bytes that can be used with the `wrap` function.
    function defaultWrapData() external returns (bytes memory);

    /// @notice Returns a default packet of bytes for use with the `unwrap` function.
    ///
    /// @return A packet of bytes that can be used with the `unwrap` function.
    function defaultUnWrapData() external returns (bytes memory);
}","keccak256":"0x90f5b9e8854027d15b740c4274ea92111dc2cc4f6d58368da3f76c80fd67e998"},"contracts/interfaces/alchemist/IAlchemistV2Actions.sol":{"content":"pragma solidity ^0.8.9;

/// @title  IAlchemistV2Actions
/// @author Alchemix Finance
interface IAlchemistV2Actions {
    /// @notice Approve an address to mint debt from the CDP owned by msg.sender;
    ///
    /// This function is intented to be used as a way to delegate a user's credit to a third party,
    /// likely another lego.
    ///
    /// @param spender The address that will be approved to mint.
    /// @param amount  The amount of the approval.
    function approveMint(address spender, uint256 amount) external;

    /// @notice Approve an address to withdraw collateral from the CDP owned by msg.sender;
    ///
    /// This function is intented to be used as a way to delegate a user's collatearl to a third party,
    /// likely another lego.
    ///
    /// @param withdrawer The address that will be approved to withdraw.
    /// @param amount  The amount of the approval.
    function approveWithdraw(address withdrawer, uint256 amount) external;

    /// @notice Sync the weights of all yieldTokens in a user's CDP. 
    ///
    /// @param owner The owner of the CDP.
    function poke(address owner) external;

    /// @notice Deposit a yield-token into a user's CDP.
    ///
    /// @param yieldToken     The yield-token to deposit.
    /// @param amount    The amount of yield-tokens to deposit.
    /// @param recipient The owner of the CDP that will receive the resulting shares.
    function deposit(address yieldToken, uint256 amount, address recipient) external;

    /// @notice Deposit an underlying-token into a user's CDP.
    ///
    /// @param yieldToken     The yield-token that accepts the target underlying-token.
    /// @param amount    The amount of the target underlying-token to deposit.
    /// @param recipient The owner of the CDP that will receive the resulting shares.
    /// @param data      A packet of bytes sent to the `wrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `deposit` function (or similar) of the target yield-token.
    function depositUnderlying(address yieldToken, uint256 amount, address recipient, bytes memory data) external;

    /// @notice Withdraw a yield-token from the CDP owned by msg.sender.
    ///
    /// @param yieldToken     The yield-token to withdraw.
    /// @param shares    The number of shares to withdraw.
    /// @param recipient The recipient of the yield-tokens that are withdrawn.
    function withdraw(address yieldToken, uint256 shares, address recipient) external;

    /// @notice Withdraw a yield-token from the CDP owned by owner.
    ///
    /// @param owner     The owner of the CDP
    /// @param token     The yield-token to withdraw.
    /// @param shares    The number of shares to withdraw.
    /// @param recipient The recipient of the yield-tokens that are withdrawn.
    function withdrawFrom(address owner, address token, uint256 shares, address recipient) external;

    /// @notice Withdraw an underlying-token from the CDP owned by msg.sender.
    ///
    /// @param yieldToken     The yield-token that can be redeemed for the target underlying-token.
    /// @param shares    The number of shares to withdraw.
    /// @param recipient The recipient of the underlying-tokens that are withdrawn.
    /// @param data      A packet of bytes sent to the `unwrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    function withdrawUnderlying(address yieldToken, uint256 shares, address recipient, bytes memory data) external;

    /// @notice Withdraw an underlying-token from the CDP owned by owner.
    ///
    /// @param owner     The owner of hte CDP
    /// @param token     The yield-token that can be redeemed for the target underlying-token.
    /// @param shares    The number of shares to withdraw.
    /// @param recipient The recipient of the underlying-tokens that are withdrawn.
    /// @param data      A packet of bytes sent to the `unwrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    function withdrawUnderlyingFrom(address owner, address token, uint256 shares, address recipient, bytes memory data) external;

    /// @notice Mint debt from the CDP owned by msg.sender.
    ///
    /// @param amount    The amount of debt-token to mint.
    /// @param recipient The recipient of the minted debt-token.
    function mint(uint256 amount, address recipient) external;

    /// @notice Mint debt from the CDP owned by owner.
    ///
    /// @param owner     The owner of the CDP.
    /// @param amount    The amount of debt-token to mint.
    /// @param recipient The recipient of the minted debt-token.
    function mintFrom(address owner, uint256 amount, address recipient) external;

    /// @notice Burn debt-token to pay back debt in the CDP owned by recipient.
    ///
    /// @param amount    The amount of debt-token to burn.
    /// @param recipient The recipient of the credit.
    function burn(uint256 amount, address recipient) external;

    /// @notice Repay debt with an underlying-token.
    ///
    /// @param underlyingToken     The underlying-token used to repay.
    /// @param amount    The amount of underlying-token to send for debt repayment (also the amount of debt repaid).
    /// @param recipient The owner of the CDP that will receive the credit.
    function repay(address underlyingToken, uint256 amount, address recipient) external;

    /// @notice Liquidate funds from the CDP owned by msg.sender.
    ///
    /// @param yieldToken  The yield-token to liquidate.
    /// @param shares The amount of shares to liquidate.
    /// @param params A packet of bytes sent to the `unwrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    function liquidate(address yieldToken, uint256 shares, bytes memory params) external;

    /// @notice Donate credit weight to a target yield-token by burning debt-tokens.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param amount The amount of debt-tokens to burn.
    function donate(address yieldToken, uint256 amount) external;

    /// @notice Harvest any pending yield from the target yield-token.
    ///
    /// @param yieldToken  The target yield-token.
    /// @param params A packet of bytes sent to the `unwrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    function harvest(address yieldToken, bytes memory params) external;
}","keccak256":"0x516351b33bd1fcb57e667234af35cb568f7171a29cf5cc17c691eb9987d04e9a"},"contracts/interfaces/alchemist/IAlchemistV2AdminActions.sol":{"content":"pragma solidity ^0.8.9;

import "../ITokenAdapter.sol";

/// @title  IAlchemistV2AdminActions
/// @author Alchemix Finance
interface IAlchemistV2AdminActions {
    /// @notice Configuration parameters for an underlying-token
    struct UnderlyingTokenConfig {
        uint256 repayLimitMaximum;          /// The maximum value for the repay LGF.
        uint256 repayLimitBlocks;           /// The number of recovery blocks for the repay LGF.
        uint256 liquidationLimitMaximum;    /// The maximum value for the liquidation LGF.
        uint256 liquidationLimitBlocks;     /// The number of recovery blocks for the liquidation LGF.
    }

    /// @notice Parameters used to register a yield-token in the Alchemsit.
    struct YieldTokenConfig {
        address adapter;                /// The adapter used by the alchemist to interact with the yield-token's deposit/withraw functions.
        uint256 maximumLoss;            /// The maximum % loss in expected value that can occur before a yield-token is automatically disabled.
        uint256 maximumExpectedValue;   /// The maximum value (in underlying-tokens) of yield-tokens that the Alchemist can hold.
    }

    /// @notice Sets the pending administrator.
    ///
    /// @dev This is the first step in the 2-step process of setting a new administrator. After this function is called,
    ///      the pending administrator will then need to call `acceptAdmin` to complete the process.
    ///
    /// @param value the address to set the pending admin to.
    function setPendingAdmin(address value) external;

    /// @notice Accepts the role of administrator.
    ///
    /// @dev This is the second step in the 2-step process of setting a new administrator.
    function acceptAdmin() external;

    /// @notice Add an underlying-token to the list of supported underlying-tokens.
    ///
    /// @param underlyingToken  The underlying-token to add.
    /// @param config A set of initial configuration parameters for the underlying-token.
    function addUnderlyingToken(address underlyingToken, UnderlyingTokenConfig calldata config) external;

    /// @notice Add a yield-token to the list of supported yield-tokens.
    ///
    /// @param yieldToken  The yield-token to add.
    /// @param config A set of initial configuration parameters for the yield-token.
    function addYieldToken(address yieldToken, YieldTokenConfig calldata config) external;

    /// @notice Enable an underlying-token.
    ///
    /// @param underlyingToken The underlying-token to enable.
    function enableUnderlyingToken(address underlyingToken) external;

    /// @notice Disable an underlying-token.
    ///
    /// @param underlyingToken The underlying-token to disable.
    function disableUnderlyingToken(address underlyingToken) external;

    /// @notice Enable a yield-token.
    ///
    /// @param yieldToken The yield-token to enable.
    function enableYieldToken(address yieldToken) external;

    /// @notice Disable a yield-token.
    ///
    /// @param yieldToken The yield-token to disable.
    function disableYieldToken(address yieldToken) external;

    /// @notice Configure a repay limiter.
    ///
    /// @param underlyingToken The underlying-token to configure.
    /// @param maximum         The maximum repay limit for the underlying-token.
    /// @param blocks          The number of blocks determining the recovery rate of the repay limit of the underlying-token.
    function configureRepayLimit(address underlyingToken, uint256 maximum, uint256 blocks) external;

    /// @notice Configure a liquidation limiter.
    ///
    /// @param underlyingToken The underlying-token to configure.
    /// @param maximum         The maximum liquidation limit for the underlying-token.
    /// @param blocks          The number of blocks determining the recovery rate of the liquidation limit of the underlying-token.
    function configureLiquidationLimit(address underlyingToken, uint256 maximum, uint256 blocks) external;

    /// @notice Set the address of the transmuter.
    ///
    /// The address set here will likely be a TransmuterBuffer contract, and not the Transmuter itself,
    /// but the Alchemist does not know nor does it care.
    ///
    /// @param value The address of the transmuter.
    function setTransmuter(address value) external;

    /// @notice Set the minimum collateralization ratio for the debt-token.
    ///
    /// @param value The new minimum collateralization ratio.
    function setMinimumCollateralization(uint256 value) external;

    /// @notice Sets the fee that the protocol will take from harvests.
    ///
    /// @param value the value to set the protocol fee to. This is in units of basis points.
    function setProtocolFee(uint256 value) external;

    /// @notice Sets the address which will receive protocol fees.
    ///
    /// @param value the address to set the protocol fee receiver to.
    function setProtocolFeeReceiver(address value) external;

    /// @notice Configure the minting limiter.
    ///
    /// @param maximum  The maximum minting limit for the Alchemist.
    /// @param rate   The number of blocks determining the recovery rate of the minting limit.
    function configureMintingLimit(uint256 maximum, uint256 rate) external;

    /// @notice Sets the maximum loss that a yield bearing token will permit before restricting certain actions.
    ///
    /// @dev There are two types of loss of value for yield bearing assets: temporary or permanent. The system will
    ///      restrict actions which are sensitive to both forms of loss. For example, deposits must be restricted
    ///      when an excessive loss is encountered to prevent users from having their collateral harvested from them.
    ///      While the user would receive credit, which then could be exchanged for value equal to the collateral
    ///      that was harvested from them, it is seen as a negative user experience because the value of their
    ///      collateral should have been higher than what was originally recorded when they made their deposit.
    ///
    /// @param yieldToken the address of the yield bearing token.
    /// @param value the value to set the maximum loss to. This is in units of basis points.
    function setMaximumLoss(address yieldToken, uint256 value) external;

    /// @notice Snap the expected value of a token to the current value.
    ///
    /// This function would only be used in the event of a loss in the target yield-token.
    /// For example, say a third-party vault experiences a 50% loss.  The expected value (amount of underlying-tokens)
    /// of the yield-tokens being held by the Alchemist would be 2X the real value that those yield-tokens could be
    /// redeemed for.  The `snap` function gives governance a way to realize those losses so that users can continue
    /// using the strategy as normal.
    ///
    /// @param yieldToken The target yield-token to snap.
    function snap(address yieldToken) external;
}","keccak256":"0x4656d43cc0ba75c7fb34abf64f1551bfacf948f1082e55224716a20ab5e8a7e1"},"contracts/interfaces/alchemist/IAlchemistV2Errors.sol":{"content":"pragma solidity ^0.8.9;

/// @title  IAlchemistV2Errors
/// @author Alchemix Finance
interface IAlchemistV2Errors {
    // #if !(DISABLE_CUSTOM_ERRORS)
    error SlippageExceeded();
    error ExpectedValueExceeded(address yieldToken, uint256 expectedValue, uint256 maximumExpectedValue);
    error LossExceeded(address yieldToken, uint256 loss, uint256 maximumLoss);
    error MintingLimitExceeded(uint256 amount, uint256 mintingLimit);
    error RepayLimitExceeded(address underlyingToken, uint256 amount, uint256 repayLimit);
    error LiquidationLimitExceeded(address underlyingToken, uint256 amount, uint256 repayLimit);
    error Undercollateralized();
    error UnsupportedToken(address token);
    error IllegalState(string message);
    error IllegalArgument(string message);
    error Unauthorized(string message);
    error TokenDisabled(address token);
    error OnlyKeeper();
    // #endif
}","keccak256":"0xbdcfaf23d66f5de4b81713d746471758cdce89c80cbfc4b9e2e5feb3d303d1e2"},"contracts/interfaces/alchemist/IAlchemistV2Events.sol":{"content":"pragma solidity ^0.8.9;

/// @title  IAlchemistV2Events
/// @author Alchemix Finance
interface IAlchemistV2Events {
    /// @notice Emitted when the admin updates the pendingAdmin.
    ///
    /// @param value The new pendingAdmin.
    event PendingAdminUpdated(address value);

    /// @notice Emitted when the pendingAdmin accepts the admin role.
    ///
    /// @param value The new admin.
    event AdminUpdated(address value);

    /// @notice Emitted when a sentinel is added.
    ///
    /// @param value The new sentinel.
    event SentinelAdded(address value);

    /// @notice Emitted when a sentinel is removed.
    ///
    /// @param value The old sentinel.
    event SentinelRemoved(address value);

    /// @notice Emitted when a keeper is added.
    ///
    /// @param value The new keeper.
    event KeeperAdded(address value);

    /// @notice Emitted when a keeper is removed.
    ///
    /// @param value The old keeper.
    event KeeperRemoved(address value);

    /// @notice Emitted when an underlying-token is added to the Alchemist.
    ///
    /// @param token The underlying-token that was added.
    event AddUnderlyingToken(address indexed token);

    /// @notice Emitted when a yield-token is added to the Alchemist.
    ///
    /// @param token The yield-token that was added.
    /// @param adapter The adapter for the yield-token.
    event AddYieldToken(address indexed token, address adapter);

    /// @notice Emitted when an underlying-token is enabled in the Alchemist.
    ///
    /// @param underlyingToken The underlying-token that was enabled.
    event UnderlyingTokenEnabled(address indexed underlyingToken);

    /// @notice Emitted when an underlying-token is disabled in the Alchemist.
    ///
    /// @param underlyingToken The underlying-token that was disabled.
    event UnderlyingTokenDisabled(address indexed underlyingToken);

    /// @notice Emitted when a yield-token is enabled in the Alchemist.
    ///
    /// @param yieldToken The yield-token that was enabled.
    event YieldTokenEnabled(address indexed yieldToken);

    /// @notice Emitted when a yield-token is disabled in the Alchemist.
    ///
    /// @param yieldToken The yield-token that was disabled.
    event YieldTokenDisabled(address indexed yieldToken);


    /// @notice Emitted when a repay limiter is updated.
    ///
    /// @param underlyingToken    The underlying-token that had its repay limiter updated.
    /// @param maximum  The new maximum repay limit for the underlying-token.
    /// @param blocks   The new number of blocks determining the recovery rate of the repay limit for the underlying-token.
    event RepayLimitUpdated(address indexed underlyingToken, uint256 maximum, uint256 blocks);

    /// @notice Emitted when a liquidation limiter is updated.
    ///
    /// @param underlyingToken    The underlying-token that had its liquidation limiter updated.
    /// @param maximum  The new maximum liquidation limit for the underlying-token.
    /// @param blocks   The new number of blocks determining the recovery rate of the liquidation limit for the underlying-token.
    event LiquidationLimitUpdated(address indexed underlyingToken, uint256 maximum, uint256 blocks);

    /// @notice Emitted with the address of the Transmuter is updated.
    ///
    /// @param value The new address of the Transmuter.
    event TransmuterUpdated(address value);

    /// @notice Emitted when the minimum collateralization ratio is updated.
    ///
    /// @param value The new minimum collateralization ratio.
    event MinimumCollateralizationUpdated(uint256 value);

    /// @notice Emitted when the protocol fee is updated.
    ///
    /// @param value The new protocol fee.
    event ProtocolFeeUpdated(uint256 value);

    /// @notice Emitted when the protocol fee receiver is updated.
    ///
    /// @param value The new protocol fee receiver.
    event ProtocolFeeReceiverUpdated(address value);

    /// @notice Emitted when an underyling-token's minting limit is updated.
    ///
    /// @param maximum The new maximum minting limit.
    /// @param blocks  The number of blocks determining the recover rate.
    event MintingLimitUpdated(uint256 maximum, uint256 blocks);

    /// @notice Emitted when a yield-token's maximum loss is updated.
    ///
    /// @param yieldToken   The target yield-token.
    /// @param value        The new maximum loss.
    event MaximumLossUpdated(address indexed yieldToken, uint256 value);

    /// @notice Emitted when a yield-token's expected value is snapped to the current value.
    ///
    /// @param yieldToken    The target yield-token.
    /// @param expectedValue The new expected value of the yield-token.
    event Snap(address indexed yieldToken, uint256 expectedValue);

    /// @notice Emitted when a CDP owner approves a spender to mint debt from their CDP.
    ///
    /// @param owner   The owner of the CDP.
    /// @param spender The spender that is approved to mint debt.
    /// @param amount  The amount of debt that the spender is approved to mint.
    event ApproveMint(address indexed owner, address indexed spender, uint256 amount);

    /// @notice Emitted when a CDP owner approves a withdrawer to withdraw collateral from their CDP.
    ///
    /// @param owner   The owner of the CDP.
    /// @param withdrawer The withdrawer that is approved to withdraw collateral.
    /// @param amount  The amount of collateral that the withdrawer is approved to withdraw.
    event ApproveWithdraw(address indexed owner, address indexed withdrawer, uint256 amount);

    /// @notice Emitted when a yield-token is successfully harvested.
    ///
    /// @param yieldToken   The yield-token that was harvested.
    /// @param params       The packet of bytes that was sent to the `unwrap` function of the yield-token-adapter.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    event Harvest(address indexed yieldToken, bytes params);

    /// @notice Emitted when a user deposits funds (yield-tokens or underlying-tokens) into a CDP.
    ///
    /// @param sender       The msg.sender that deposited funds.
    /// @param yieldToken   The yield-token that was deposited.
    /// @param amount       The amount of yield-tokens that were deposited.
    /// @param recipient    The owner of the CDP that recieved the deposit.
    event Deposit(address indexed sender, address indexed yieldToken, uint256 amount, address recipient);

    /// @notice Emitted when a user withdraws funds (yield-tokens or underlying-tokens) from a CDP.
    ///
    /// @param sender       The owner of the CDP, msg.sender.
    /// @param yieldToken   The yield-token that was withdrawn.
    /// @param shares       The amount of shares that were withdrawn.
    /// @param recipient    The address that received the withdrawn funds.
    event Withdraw(address indexed sender, address indexed yieldToken, uint256 shares, address recipient);

    /// @notice Emitted when debt-tokens are minted.
    ///
    /// @param owner     The owner of the CDP.
    /// @param amount    The amount of debt-tokens minted.
    /// @param recipient The recipient of the minted debt-tokens.
    event Mint(address indexed owner, uint256 amount, address recipient);

    /// @notice Emitted when debt-tokens are burned to repay debt.
    ///
    /// @param sender    The user that burned the debt-tokens, msg.sender.
    /// @param amount    The amount of debt-tokens burned.
    /// @param recipient The recipient of the resulting credit gained.
    event Burn(address indexed sender, uint256 amount, address recipient);

    /// @notice Emitted when underlying-tokens are used to repay a CDP's debt.
    ///
    /// @param sender               The user that repaid the debt.
    /// @param underlyingToken      The underlying-token used to repay the debt.
    /// @param amount               The amount of underlying-tokens paid to wipe debt.
    /// @param recipient            The recipient of the resulting credit gained.
    event Repay(address indexed sender, address indexed underlyingToken, uint256 amount, address recipient);

    /// @notice Emitted when a user liquidates shares from their CDP.
    ///
    /// @param sender       The owner of the CDP, msg.sender.
    /// @param yieldToken   The yield-token to liquidate.
    /// @param shares       The number of shares to liquidate.
    event Liquidate(address indexed sender, address indexed yieldToken, uint256 shares);

    /// @notice Emitted when a user donates credit (via burning debt-tokens) to a yield-token.
    ///
    /// @param sender The user that donated the credit, msg.sender.
    /// @param yieldToken  The yield-token to which credit was donated.
    /// @param amount The amount of debt-token burned to donate credit.
    event Donate(address indexed sender, address indexed yieldToken, uint256 amount);
}","keccak256":"0xde99b98fcafee9be73d12e4cf7fc1199f4ee8c5fd2e16fd8fae1f23698f1f5b4"},"contracts/interfaces/alchemist/IAlchemistV2Immutables.sol":{"content":"pragma solidity ^0.8.9;

/// @title  IAlchemistV2Immutables
/// @author Alchemix Finance
interface IAlchemistV2Immutables {
    /// @notice Returns the address of the debt-token used by the Alchemist.
    ///
    /// @return The address of the debt-token.
    function debtToken() external view returns (address);
}","keccak256":"0xbd1cc86a7e0558d8554b5756006c9bd8c976890bf33fbb2bf30d2c8b31dbaaca"},"contracts/interfaces/alchemist/IAlchemistV2Permit.sol":{"content":"pragma solidity ^0.8.9;

/// @title  IAlchemistV2Permit
/// @author Alchemix Finance
interface IAlchemistV2Permit {
    // #if !(DISABLE_CUSTOM_ERRORS)
    /// @notice An error which is thrown when a permit signature deadline is exceeded.
    error SignatureDeadlineExceeded();

    /// @notice An error which is thrown when an invalid signature is given.
    error InvalidSignature();
    // #endif

    /// @notice Calculates the EIP-712 domain separator used in permits.
    ///
    /// @dev The domain separator is calculated instead of being cached because of the potential for replay attack
    ///      when a chain split occurs. This domain separator is expected to be unique as per the EIP-712 specification.
    ///
    /// @return the domain separator.
    function DOMAIN_SEPARATOR() external view returns (bytes32);

    /// @notice Gets the type hash used in the permit minting signature verification.
    ///
    /// @return the permit mint typehash.
    function PERMIT_MINT_TYPEHASH() external view returns (bytes32);

    /// @notice Gets the type hash used in the permit withdrawing signature verification.
    ///
    /// @return the permit withdraw typehash.
    function PERMIT_WITHDRAW_TYPEHASH() external view returns (bytes32);

    /// @notice Gets the current permit nonce of `account`.
    ///
    /// @dev This is used to prevent replay attacks. All permit schemes share the same nonce.
    ///
    /// @param account the account to get the nonce of.
    /// @return the nonce.
    function nonces(address account) external view returns (uint256);

    /// @notice Approves `spender` to spend `value` tokens on the behalf of `owner`..
    ///
    /// The permit message should be signed off-chain by the owner of the position and the resulting v, r, and s
    /// parameters are used to verify the approval signature.
    ///
    /// @param owner    the account that is being approved.
    /// @param spender  the account which is being permitted to spend.
    /// @param value    the amount to permit the spender to spend on behalf of the owner.
    /// @param deadline the deadline timestamp by which the call must be mined for the approve to work.
    /// @param v        must produce valid secp256k1 signature from the holder along with `r` and `s`.
    /// @param r        must produce valid secp256k1 signature from the holder along with `v` and `s`.
    /// @param s        must produce valid secp256k1 signature from the holder along with `r` and `v`.
    function permitMint(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /// @notice Approves `withdrawer` to withdraw `value` tokens on the behalf of `owner`..
    ///
    /// The permit message should be signed off-chain by the owner of the position and the resulting v, r, and s
    /// parameters are used to verify the approval signature.
    ///
    /// @param owner    the account that is being approved.
    /// @param withdrawer the account which is being permitted to swithdraw.
    /// @param value    the amount to permit the spender to spend on behalf of the owner.
    /// @param deadline the deadline timestamp by which the call must be mined for the approve to work.
    /// @param v        must produce valid secp256k1 signature from the holder along with `r` and `s`.
    /// @param r        must produce valid secp256k1 signature from the holder along with `v` and `s`.
    /// @param s        must produce valid secp256k1 signature from the holder along with `r` and `v`.
    function permitWithdraw(
        address owner,
        address withdrawer,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;
}","keccak256":"0xbb3988dad2c2c886b251e29c6ec51e8d62360f86042bb385fc0ba86c93818711"},"contracts/interfaces/alchemist/IAlchemistV2State.sol":{"content":"pragma solidity ^0.8.9;

/// @title  IAlchemistV2State
/// @author Alchemix Finance
interface IAlchemistV2State {
    /// @notice Gets the admin of the Alchemist.
    ///
    /// @return the admin.
    function admin() external view returns (address);

    /// @notice Gets the pending administrator of the Alchemist.
    ///
    /// @return the pending administrator.
    function pendingAdmin() external view returns (address);

    /// @notice Checks if an address is a sentinel.
    ///
    /// @return the true or false.
    function sentinels(address value) external view returns (bool);

    /// @notice Checks if an address is a keeper.
    ///
    /// @return the true or false.
    function keepers(address value) external view returns (bool);

    /// @notice Gets the transmuter.
    ///
    /// @return the transmuter.
    function transmuter() external view returns (address);

    /// @notice Gets the minimum collateralization that the system supports.
    ///
    /// Collateralization is determined by taking the total value of collateral that a user has deposited into their
    /// account and dividing it by the debt that they have taken out.
    ///
    /// @dev The value returned is a 18 decimal fixed point integer.
    ///
    /// @return the minimum collateralization.
    function minimumCollateralization() external view returns (uint256);

    /// @notice Gets the protocol fee.
    ///
    /// @return the protocol fee.
    function protocolFee() external view returns (uint256);

    /// @notice Gets the protocol fee receiver.
    ///
    /// @return the protocol fee receiver.
    function protocolFeeReceiver() external view returns (address);

    /// @notice Gets the rate of the underlying tokens per share.
    ///
    /// @param yieldToken the token to check.
    ///
    /// @return the rate of the underlying tokens per share.
    function underlyingTokensPerShare(address yieldToken) external view returns (uint256);

    /// @notice Gets the rate of the yield tokens per share.
    ///
    /// @param yieldToken the yield-token to check.
    ///
    /// @return the rate of the yield tokens per share.
    function yieldTokensPerShare(address yieldToken) external view returns (uint256);

    /// @notice Gets the supported underlying tokens.
    ///
    /// @dev The order of the entries returned by this function is not guaranteed to be consistent between calls.
    ///
    /// @return the supported underlying tokens.
    function getSupportedUnderlyingTokens() external view returns (address[] memory);

    /// @notice Gets the supported yield tokens.
    ///
    /// @dev The order of the entries returned by this function is not guaranteed to be consistent between calls.
    ///
    /// @return the supported yield tokens.
    function getSupportedYieldTokens() external view returns (address[] memory);

    /// @notice Gets if an underlying token is supported.
    ///
    /// @param underlyingToken the token to check.
    ///
    /// @return if the token is supported.
    function isSupportedUnderlyingToken(address underlyingToken) external view returns (bool);

    /// @notice Gets if a yield token is supported.
    ///
    /// @param yieldToken the token to check.
    ///
    /// @return if the token is supported.
    function isSupportedYieldToken(address yieldToken) external view returns (bool);

    /// @notice Gets the debt and deposited tokens of an account.
    ///
    /// @param owner the address that owns the account.
    ///
    /// @return debt            the last recorded amount of debt that the account had incurred.
    /// @return depositedTokens the yield tokens that the owner has deposited into the system.
    function accounts(address owner) external view returns (int256 debt, address[] memory depositedTokens);

    /// @notice Gets the balance and lastAccruedWeight of a given yield-token in a user's CDP
    ///
    /// @param owner The CDP owner.
    /// @param yieldToken The yield-token.
    ///
    /// @return balance           The amount of shares of the yield-token in the CDP.
    /// @return lastAccruedWeight The last-accrued-weight of the yield-token in the CDP.
    function positions(address owner, address yieldToken) external view returns (uint256 balance, uint256 lastAccruedWeight);

    /// @notice Gets parameters for an underlying token.
    ///
    /// @param underlyingToken the address of the underlying token.
    ///
    /// @return decimals      the number of decimals that the token has.
    /// @return scalingFactor the scaling factor used to convert the token into units of the decimal token.
    /// @return enabled       a flag indicating if the token is enabled.
    function getUnderlyingTokenParameters(address underlyingToken)
        external view
        returns (
            uint8 decimals,
            uint256 scalingFactor,
            bool enabled
        );

    /// @notice Get the parameters and state of a yield-token.
    ///
    /// @param yieldToken the address of the yield token.
    ///
    /// @return decimals        The amount of decimals the yield-token has.
    /// @return underlyingToken The underlying-token.
    /// @return adapter         The adapter for the underlying-token.
    /// @return maximumLoss     The maximum loss that the yield-token can experience before being automatically disabled.
    /// @return balance         The amount of yield-tokens held by the Alchemist.
    /// @return totalShares     The total amount of shares for of the yield-token.
    /// @return expectedValue   The expected amount of underlying-tokens that the Alchemist would receive from redeeming its balance of yield-token.
    /// @return accruedWeight   The accured weight of shares of the yield-token.
    /// @return enabled         A flag indicating if the yield-token is enabled.
    function getYieldTokenParameters(address yieldToken)
        external view
        returns (
            uint8 decimals,
            address underlyingToken,
            address adapter,
            uint256 maximumLoss,
            uint256 balance,
            uint256 totalShares,
            uint256 expectedValue,
            uint256 accruedWeight,
            bool enabled
        );

    /// @notice Gets the amount of tokens an account that has been delegated credit can mint.
    ///
    /// @param owner the owner of a position in alchemix that has delegated credit.
    /// @param spender the account which has been delegated credit.
    ///
    /// @return the amount of tokens the account that has been delegated credit can mint.
    function mintAllowance(address owner, address spender) external view returns (uint256);

    /// @notice Gets the amount of tokens an account has been permitted can withdraw.
    ///
    /// @param owner the owner of a position in alchemix.
    /// @param spender the account which has been permitted to withdraw.
    ///
    /// @return the amount of tokens the account that has been permitted to withdraw can withdraw.
    function withdrawAllowance(address owner, address spender) external view returns (uint256);
}","keccak256":"0xa2c6f698bd18d7db49186eb5417b986c9133bac675bb8eb591073c04e1dec63e"},"contracts/interfaces/transmuter/ITransmuterBuffer.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.9;
import {IDetailedERC20} from "../IDetailedERC20.sol";
import {ITransmuterV2} from "./ITransmuterV2.sol";
import {AlchemistV2} from "../../AlchemistV2.sol";

/// @title ITransmuterBuffer
/// @author Alchemix Finance
/// @notice An interface contract to buffer funds between the Alchemist and the Transmuter
interface ITransmuterBuffer {
  // #if !(DISABLE_CUSTOM_ERRORS)
  error IllegalState(string message);
  error NoBurnableCredit();
  error Unauthorized();
  error OnlyKeeper();
  // #endif


  /// @notice Parameters used to define a given weighting schema.
  /// Weighting schemas can be used to generally weight assets in relation to an action or actions that will be taken.
  /// In the TransmuterBuffer, there are 2 actions that require weighting schemas: `burnCredit` and `depositFunds`.
  ///
  /// `burnCredit` uses a weighting schema that determines which yield-tokens are targeted when burning credit from
  /// the CDP controlled by the TransmuterBuffer, via the `Alchemist.donate` function.
  ///
  /// `depositFunds` uses a weighting schema that determines which yield-tokens are targeted when depositing
  /// underlying-tokens into the Alchemist.
  struct Weighting {
    mapping(IDetailedERC20 => uint256) weights;   /// The weights of the tokens used by the schema.
    IDetailedERC20[] tokens;                      /// The tokens used by the schema.
    uint256 totalWeight;                          /// The total weight of the schema (sum of the token weights).
  }

  /// @notice Emitted when the Alchemist is set.
  ///
  /// @param alchemist the new Alchemist.
  event SetAlchemist(address alchemist);

  /// @notice Emitted when an underlying token is registered.
  ///
  /// @param underlyingToken the underlying token.
  /// @param transmuter the transmuter for the underlying token.
  event RegisterAsset(address underlyingToken, address transmuter);

  /// @notice Emitted when an underlying token is de-registered.
  ///
  /// @param underlyingToken the underlying token.
  event DeRegisterAsset(address underlyingToken);

  /// @notice Emitted when an underlying token's flow rate is updated.
  ///
  /// @param underlyingToken the underlying token.
  /// @param flowRate the flow rate for the underlying token.
  event SetFlowRate(address underlyingToken, uint256 flowRate);

  /// @notice Emitted when the strategies are refreshed.
  event RefreshStrategies();

  /// @notice Set alchemist by admin.
  ///
  /// This function reverts if the caller is not the current admin.
  ///
  /// @param _alchemist the new alchemist who'se funds we are handling.
  function setAlchemist(AlchemistV2 _alchemist) external;

  /// @notice Registers an underlying-token.
  ///
  /// This function reverts if the caller is not the current admin.
  ///
  /// @param underlyingToken the underlying-token being registered.
  /// @param _transmuter the transmuter for the underlying-token.
  function registerAsset(IDetailedERC20 underlyingToken, address _transmuter) external;

  /// @notice De-registers an underlying-token.
  ///
  /// This function reverts if the caller is not the current admin.
  ///
  /// @param underlyingToken the underlying-token being registered.
  function deRegisterAsset(IDetailedERC20 underlyingToken) external;

  /// @notice Set flowrate of an ERC20 by admin.
  ///
  /// This function reverts if the caller is not the current admin.
  ///
  /// @param underlyingToken the underlying-token getting the flow-rate set.
  /// @param _flowrate amount of tokens withdrawable from the alchemist and forwardable to the public transmuter.
  function setFlowrate(IDetailedERC20 underlyingToken, uint256 _flowrate) external;

  /// @notice Withdraws collateral from the alchemist
  ///
  /// This function reverts if:
  ///   1. the caller is not the transmuter
  ///   2. there is not enough flow available to fulfill the request
  ///   3. there is not enough underlying collateral in the alchemist controlled by the buffer to fullfil the request
  ///
  /// @param underlyingToken The underlying-token to withdraw
  /// @param amount The amount to withdraw
  /// @param recipient The account receiving the withdrawn funds
  function withdraw(
    IDetailedERC20 underlyingToken,
    uint256 amount,
    address recipient
  ) external;

  /// @notice A hook called when the Alchemist sends underlying-tokens to the TransmuterBuffer.
  ///
  /// @param underlyingToken The underlying-token that was sent.
  /// @param amount The amount of underlying-token that was sent.
  function onERC20Received(IDetailedERC20 underlyingToken, uint256 amount) external;

  /// @notice Gets the total amount of underlying-token that the TransmuterBuffer controls in the Alchemist.
  ///
  /// @param underlyingToken The underlying-token to query.
  function getTotalUnderlyingBuffered(IDetailedERC20 underlyingToken) external view returns (uint256 totalBuffered);

  /// @notice Refresh the yield-tokens in the TransmuterBuffer.
  ///
  /// This requires a call anytime governance adds a new yield-token to the alchemist.
  function refreshStrategies() external;

  /// @notice Gets the total available flow for the underlying token
  ///
  /// The "total available flow" will return the lesser of:
  ///   1) `flowAvailable[token]`
  ///   2) the result of `getTotalUnderlyingBuffered`.
  ///
  /// @param underlyingToken The underlying-token to query
  function getAvailableFlow(IDetailedERC20 underlyingToken) external view returns (uint256);

  /// @notice Sets up a weighting schema
  ///
  /// @param weightType The name of the weighting schema
  /// @param tokens The yield-tokens to weight
  /// @param weights The weights of the yield-tokens
  function setWeights(string calldata weightType, IDetailedERC20[] memory tokens, uint256[] memory weights) external;

  /// @notice Sets the `weightings` map key for an underlying-token
  ///
  /// @param underlyingToken The underlying-token.
  /// @param weightType The key for accessing the underlying-token's weight mapping
  function setUnderlyingWeightType(address underlyingToken, string calldata weightType) external;

  /// @notice Gets the credit held by the TransmuterBuffer
  ///
  function getCredit() external view returns (uint256 credit);

  /// @notice Burns available credit in the alchemist.
  ///
  function burnCredit() external;

  /// @notice Deposits local collateral into the alchemist
  ///
  /// @param underlyingToken the collateral to deposit.
  /// @param amount the amount to deposit.
  function depositFunds(address underlyingToken, uint256 amount) external;

  /// @notice Gets the weight of the given weight type and token
  ///
  /// @param weightType the type of weight to query
  /// @param token the weighted token
  ///
  /// @return weight the weight of the token for the given weight type
  function getWeight(string calldata weightType, address token) external view returns (uint256 weight);
}
