import "../interfaces/IERC20Extensions.sol";
import "../interfaces/IERC20Meta.sol";
import "../interfaces/IERC20Minimal.sol";
library TokenUtils {
    /// @dev A safe function to get the decimals of an ERC20 token.
    ///
    /// @param token The target token.
    ///
    /// @return The amount of decimals of the token.
    function expectDecimals(address token) internal view returns (uint8) {
        (bool success, bytes memory data) = token.staticcall(abi.encodeWithSelector(IERC20Meta.decimals.selector));
        require(success && data.length >= 32); // FIXME: Error
        return abi.decode(data, (uint8));
    }

    /// @dev Gets the balance of tokens held by an account.
    ///
    /// @param token The token to check the balance of.
    /// @param account The address of the token holder.
    ///
    /// @return The balance of the tokens held by an account.
    function safeBalanceOf(address token, address account) internal view returns (uint256) {
        (bool success, bytes memory data) = token.staticcall(abi.encodeWithSelector(IERC20Minimal.balanceOf.selector, account));
        require(success && data.length >= 32); // FIXME: Error
        return abi.decode(data, (uint256));
    }

    /// @dev Transfers tokens to another address.
    ///
    /// @param token The token to transfer.
    /// @param recipient The address of the recipient.
    /// @param amount The amount of tokens to transfer.
    function safeTransfer(address token, address recipient, uint256 amount) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20Minimal.transfer.selector, recipient, amount));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TF'); // FIXME: Error
    }

    /// @dev Approves tokens for the smart contract.
    ///
    /// @param token The token to approve.
    /// @param spender The contract to spend the tokens.
    /// @param value The amount of tokens to approve.
    function safeApprove(address token, address spender, uint256 value) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20Minimal.approve.selector, spender, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TF'); // FIXME: Error
    }

    /// @dev Transfer tokens from one address to another address.
    ///
    /// @param token The token to transfer.
    /// @param owner The address of the owner.
    /// @param recipient The address of the recipient.
    /// @param amount The amount of tokens to transfer.
    function safeTransferFrom(address token, address owner, address recipient, uint256 amount) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20Minimal.transferFrom.selector, owner, recipient, amount));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TF'); // FIXME: Error
    }

    /// @dev Mints tokens to an address.
    ///
    /// @param token The token to mint.
    /// @param recipient The address of the recipient.
    /// @param amount The amount of tokens to mint.
    function safeMint(address token, address recipient, uint256 amount) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20Extensions.mint.selector, recipient, amount));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TF'); // FIXME: Error
    }

    /// @dev Burns tokens from its total supply.
    ///
    /// @param token  The token to burn.
    /// @param owner  The owner of the tokens.
    /// @param amount The amount of tokens to burn.
    function safeBurnFrom(address token, address owner, uint256 amount) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20Extensions.burnFrom.selector, owner, amount));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TF'); // FIXME: Error
    }
}","keccak256":"0x610fd477abc3e0a9df1ee9cfc40170708c0fa18bb89f7f5b483c8d31d344c0e1"}},"version":1}
