import "../../interfaces/ITokenAdapter.sol";
import "../../interfaces/yearn/VaultAPI.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../../interfaces/IDetailedERC20.sol";
import "../../libraries/TokenUtils.sol";

contract YearnTokenAdapter is ITokenAdapter {
    using SafeERC20 for IERC20;
    uint256 public constant MAX_BPS = 10000;
    address public override token;
    address public override underlyingToken;

    struct UnWrapParams {
        uint256 maxLoss;
    }

    constructor(address _token, address _underlyingToken) {
        token = _token;
        underlyingToken = _underlyingToken;
    }

    /// @inheritdoc ITokenAdapter
    function price() external view override returns (uint256) {
        return VaultAPI(token).pricePerShare();
    }

    /// @inheritdoc ITokenAdapter
    function wrap(uint256 amount, address recipient, bytes memory data) external override returns (uint256) {
        IERC20(underlyingToken).safeTransferFrom(msg.sender, address(this), amount);
        TokenUtils.safeApprove(underlyingToken, token, amount);
        return VaultAPI(token).deposit(amount, recipient);
    }

    /// @inheritdoc ITokenAdapter
    function unwrap(uint256 amount, address recipient, bytes memory data) external override returns (uint256) {
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        UnWrapParams memory params;
        (params) = abi.decode(data, (UnWrapParams));
        uint256 balBefore = VaultAPI(token).balanceOf(address(this));
        uint256 amountWithdrawn = VaultAPI(token).withdraw(amount, recipient, params.maxLoss);
        uint256 balAfter = VaultAPI(token).balanceOf(address(this));
        require(balBefore - balAfter == amount, "yearn: exact amount not withdrawn");
        return amountWithdrawn;
    }

    /// @inheritdoc ITokenAdapter
    function defaultWrapData() external pure override returns (bytes memory) {
