interface ITokenAdapter {
    /// @notice Gets the address of the yield-token used by the adapter.
    ///
    /// @return The address of the yield-token.
    function token() external view returns (address);

    /// @notice Gets the address of the underlying-token used by the adapter.
    ///
    /// @return The address of the underlying-token.
    function underlyingToken() external view returns (address);

    /// @notice Gets the current price of yield-token in underlying-tokens.
    ///
    /// @return The price (yield-tokens per underlying-token).
    function price() external view returns (uint256);

    /// @notice Deposits underlying-token into yield-token.
    ///
    /// @param amount    The amount of underlying-token to deposit.
    /// @param recipient The recipient of the resulting yield-tokens.
    /// @param data      A packet of bytes used by the adapter to complete the deposit action.  The format of these bytes is dependent on the `deposit` function (or similar) of the target yield-token.
    ///
    /// @return The amount of yield-token received.
    function wrap(uint256 amount, address recipient, bytes memory data) external returns (uint256);

    /// @notice Withdraws underlying-token from yield-token.
    ///
    /// @param amount    The amount of yield-tokens to redeem.
    /// @param recipient The recipient of the resulting underlying-tokens.
    /// @param data      A packet of bytes used by the adapter to complete the withdraw action.  The format of these bytes is dependent on the `withdraw` function (or similar) of the target yield-token.
    ///
    /// @return The amount of underlying-token received.
    function unwrap(uint256 amount, address recipient, bytes memory data) external returns (uint256);

    /// @notice Returns a default packet of bytes for use with the `wrap` function.
    ///
    /// @return A packet of bytes that can be used with the `wrap` function.
    function defaultWrapData() external returns (bytes memory);

    /// @notice Returns a default packet of bytes for use with the `unwrap` function.
    ///
    /// @return A packet of bytes that can be used with the `unwrap` function.
    function defaultUnWrapData() external returns (bytes memory);
