    function expectDecimals(address token) internal view returns (uint8) {
        (bool success, bytes memory data) = token.staticcall(abi.encodeWithSelector(IERC20Meta.decimals.selector));
        require(success && data.length >= 32); // FIXME: Error
        return abi.decode(data, (uint8));
